const item = $input.first().json;

let html = item.htmlBody || '';
const title = (item.title || '').trim();

// ---- Normalize lightweight HTML for consistent output ---
html = html
  // Ensure that <hr> closes automatically if necessary (depending on the render location).
  .replace(/<hr\s*>/gi, '<hr />')
  // Normalize line breaks between blocks.
  .replace(/>\s+</g, '><')
  .trim();

// --- Convert HTML to plain text (good enough for excerpts, searches, and word counts) ---
function htmlToText(inputHtml) {
  if (!inputHtml) return '';
  return inputHtml
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(p|div|h1|h2|h3|h4|h5|h6|li|ul|ol|blockquote|section|article)>/gi, '\n')
    .replace(/<hr\s*\/?>/gi, '\n---\n')
    .replace(/<[^>]+>/g, '')         // strip tags
    .replace(/[ \t]+\n/g, '\n')
    .replace(/\n{3,}/g, '\n\n')
    .trim();
}

const textBody = htmlToText(html);

// word count (separated by whitespace)
const words = textBody
  .replace(/[‚Äú‚Äù"']/g, '')
  .split(/\s+/)
  .filter(Boolean);

const wordCount = words.length;
const readingTimeMin = Math.max(1, Math.round(wordCount / 200)); // 200 wpm

// The excerpt should be 160-200 characters long (depending on your preference).
function makeExcerpt(text, maxLen = 180) {
  const t = (text || '').replace(/\s+/g, ' ').trim();
  if (t.length <= maxLen) return t;
  return t.slice(0, maxLen).replace(/\s+\S*$/, '').trim() + '‚Ä¶';
}
const excerpt = makeExcerpt(textBody, 180);

// Vietnamese slug -> Latin
function slugifyVN(str) {
  if (!str) return '';
  return str
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')  // remove accents
    .replace(/ƒë/g, 'd')
    .replace(/ƒê/g, 'd')
    .toLowerCase()
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/^-+|-+$/g, '')
    .replace(/-+/g, '-');
}

const slug = slugifyVN(title);

// output final
return [{
  json: {
    fileId: item.fileId,
    title,
    slug,
    excerpt,
    htmlBody: html,
    textBody,
    bodyLength: html.length,
    wordCount,
    readingTimeMin,
    updatedAt: new Date().toISOString(),
  }
}];

