function extractJson(text) {
  if (!text || typeof text !== 'string') return null;

  const m = text.match(/```json\s*([\s\S]*?)\s*```/i) || text.match(/```\s*([\s\S]*?)\s*```/);
  const candidate = m?.[1] ?? text;

  // Try parsing directly; if that fails, try cutting from { ... }
  try { return JSON.parse(candidate); } catch (e) {}

  const start = candidate.indexOf('{');
  const end = candidate.lastIndexOf('}');
  if (start !== -1 && end !== -1 && end > start) {
    return JSON.parse(candidate.slice(start, end + 1));
  }
  return null;
}

return $input.all().map(item => {
  const j = item.json;

  const postId = Number(j.postId); // <- retrieved from SET - SEO Input via Merge
  if (!Number.isFinite(postId) || postId <= 0) throw new Error(`Missing postId after merge`);

  const raw = j.content?.parts?.[0]?.text ?? '';
  const parsed = extractJson(raw) ?? {};

  return {
    json: {
      postId,
      seo_title: parsed.seo_title ?? '',
      meta_description: parsed.meta_description ?? '',
      focus_keyword: parsed.focus_keyword ?? '',
      slug: parsed.slug ?? j.slug ?? '',
      _debug_rawText: raw,
      htmlBody: j.htmlBody ?? '',
      textBody: j.textBody ?? '',
    }
  };
});

function clampText(s, max) {
  s = (s ?? '').toString().trim();
  if (!s) return '';
  return s.length > max ? s.slice(0, max - 1).trimEnd() + '‚Ä¶' : s;
}

function removeAccents(str) {
  return (str ?? '')
    .toString()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/ƒë/g, 'd')
    .replace(/ƒê/g, 'D');
}

function norm(str) {
  return removeAccents(str).toLowerCase().replace(/\s+/g, ' ').trim();
}

// Inject keywords at the beginning of the text (preferably inserting them in the first <p>). Use markers to prevent duplicate insertions during playback.
function injectKeywordToHtml({ html, keyword }) {
  html = (html ?? '').toString();
  keyword = (keyword ?? '').toString().trim();
  if (!html || !keyword) return html;

  const marker = `<!--n8n_kw_injected:${removeAccents(keyword).toLowerCase()}-->`;
  if (html.includes(marker)) return html;

  // If the content already has keywords, just check the "heading".
  const hasKw = norm(html).includes(norm(keyword));

  // Use the first 250 characters (light stripe tag) to check "beginning".
  const headText = html
    .slice(0, 800)
    .replace(/<[^>]+>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();

  const kwInBeginning = norm(headText).includes(norm(keyword));

  // If the keyword is already in the content and in the first paragraph -> do nothing.
  if (hasKw && kwInBeginning) return html;

  // If there is a <p> at the beginning: insert the keyword at the beginning of the <p>
  if (/<p\b[^>]*>/i.test(html)) {
    html = html.replace(/<p\b([^>]*)>/i, (m, attrs) => {
      // If the keyword is not already at the beginning of the article, add a keyword prefix.
      return `<p${attrs}><strong>${keyword}</strong>: `;
    });
  } else {
    // Without <p>, prepend 1 paragraph
    html = `<p><strong>${keyword}</strong>: </p>` + html;
  }

  // If the keyword is not already present in the content, inserting it this way will also create an occurrence in the content.
  return marker + html;
}

return $input.all().map((item) => {
  const j = item.json;

  const postId = Number(j.postId);
  if (!Number.isFinite(postId) || postId <= 0) {
    throw new Error(`Missing/invalid postId in Build WP Payload. postId=${j.postId}`);
  }

  const seo_title = clampText(j.seo_title, 70);
  const meta_description = clampText(j.meta_description, 170);
  const focus_keyword = (j.focus_keyword ?? '').toString().trim();
  const slug = (j.slug ?? '').toString().trim();

  const meta = {
    rank_math_title: seo_title,
    rank_math_description: meta_description,
    rank_math_focus_keyword: focus_keyword,
  };

  Object.keys(meta).forEach((k) => {
    if (!meta[k]) delete meta[k];
  });

  const htmlBody = j.htmlBody ?? '';
  const contentPatched = injectKeywordToHtml({ html: htmlBody, keyword: focus_keyword });

  return {
    json: {
      postId,
      slug,
      meta,
      contentPatched,
      _seo: { seo_title, meta_description, focus_keyword },
    },
  };
});
