// Extract message data from Facebook webhook
const entry = $input.first().json.body?.entry?.[0];
const messaging = entry?.messaging?.[0];

const userId = messaging?.sender?.id;
const pageId = messaging?.recipient?.id;
const messageText = messaging?.message?.text;
const timestamp = messaging?.timestamp || Date.now();
const messageId = messaging?.message?.mid;

// Store in workflow static data for batching
const staticData = $getWorkflowStaticData('global');

if (!staticData.messageBatches) {
  staticData.messageBatches = {};
}

if (!staticData.messageBatches[userId]) {
  staticData.messageBatches[userId] = {
    messages: [],
    firstMessageTime: timestamp,
    pageId: pageId
  };
}

staticData.messageBatches[userId].messages.push({
  text: messageText,
  timestamp: timestamp,
  messageId: messageId
});

return {
  json: {
    userId,
    pageId,
    messageText,
    timestamp,
    messageId,
    batchCount: staticData.messageBatches[userId].messages.length
  }
};

// Retrieve all batched messages for this user
// Get userId from Store node directly (not from input, which is HTTP response)
const userId = $('Store Message for Batching').first().json.userId;
const pageId = $('Store Message for Batching').first().json.pageId;

const staticData = $getWorkflowStaticData('global');
const userBatch = staticData.messageBatches?.[userId];

if (!userBatch || userBatch.messages.length === 0) {
  return { json: { skip: true } };
}

// Combine all messages into one
const combinedMessage = userBatch.messages
  .sort((a, b) => a.timestamp - b.timestamp)
  .map(m => m.text)
  .join(' ');

// Clear the batch
delete staticData.messageBatches[userId];

return {
  json: {
    userId,
    pageId,
    combinedMessage,
    messageCount: userBatch.messages.length,
    skip: false
  }
};

// Format AI response for Facebook Messenger
const aiResponse = $input.first().json.output || $input.first().json.text || '';
const userId = $('Retrieve Batched Messages').first().json.userId;
const pageId = $('Retrieve Batched Messages').first().json.pageId;

// Messenger has a 2000 character limit per message
let formattedResponse = aiResponse.trim();

// If response is too long, truncate it
if (formattedResponse.length > 1900) {
  formattedResponse = formattedResponse.substring(0, 1897) + '...';
}

// Remove any markdown formatting that doesn't render well in Messenger
formattedResponse = formattedResponse
  .replace(/\*\*(.+?)\*\*/g, '$1')  // Remove bold
  .replace(/\*(.+?)\*/g, '$1')      // Remove italic
  .replace(/```[\s\S]*?```/g, (match) => match.replace(/```\w*\n?/g, ''))  // Remove code blocks
  .replace(/`(.+?)`/g, '$1');       // Remove inline code

return {
  json: {
    userId,
    pageId,
    response: formattedResponse
  }
};
