const req = $('When Executed by Another Workflow').first().json;
const tables = $('Get All Tables').all().map(i => i.json);

// Find biggest table capacity
const maxCapacity = Math.max(...tables.map(t => t.Capacity));

// Binary check: can we handle this party?
const canHandle = req.partyCount <= maxCapacity;

return [{ json: { 
  canHandle: canHandle,
  maxCapacity: maxCapacity,
  requestedParty: req.partyCount
}}];

const req = $('Convert Time to UTC').first().json;
const tables = $('Get All Tables').all().map(i => i.json);
const bookings = $('Get Availability').all().map(i => i.json);

const blocked = new Set(bookings.flatMap(b => b["Table ID (from Tables List)"] || []));

const candidates = tables
  .filter(t => !blocked.has(t["Table ID"]) && t.Capacity >= req.partyCount)
  .sort((a, b) => a.Capacity - b.Capacity);

return [{ json: { 
  available: candidates.length > 0,
  reason: candidates.length > 0 ? "table_found" : "all_booked",
  tables: candidates
}}];

const availabilityData = $('Table-Availability Selector').first().json;

return [{
  json: {
    available: true,
    tableIds: availabilityData.tables.map(t => t.id),
    tableDetails: availabilityData.tables
  }
}];

