// Convert pickup time from Kyiv to UTC
const pickUpTimeUTC = new Date(req.pickUpTime + offsetStr).toISOString();

// Generate 8-digit order ID
const orderId = Math.floor(10000000 + Math.random() * 90000000);

return [{
  json: {
    orderId: orderId,
    name: req.name,
    phone: req.phone,
    items: req.items,
    pickUpTime: pickUpTimeUTC,
    priceTotal: req.priceTotal,
    notes: req.notes
  }
