// Inputs
const order = $('When Executed by Another Workflow').first().json.items;
const menu = $('Fetch Menu').all().map(i => i.json);

// Validation
if (!Array.isArray(order)) {
  return [{ json: { status: false, error: "Order must be an items array" } }];
}

let total = 0;
const breakdown = [];

for (const item of order) {
  if (!item.name || !item.quantity) {
    return [{ json: { status: false, error: "Each order item must have name and quantity" } }];
  }

  const menuItem = menu.find(
    m => m["Item Name"]?.toLowerCase().trim() === item.name.toLowerCase().trim()
  );

  if (!menuItem) {
    return [{ json: { status: false, error: `Item not found in menu: ${item.name}` } }];
  }

  if (menuItem.Available !== true) {
    return [{ json: { status: false, error: `Item not available: ${menuItem["Item Name"]}` } }];
  }

  if (typeof menuItem.Price !== "number") {
    return [{ json: { status: false, error: `Invalid price for item: ${menuItem["Item Name"]}` } }];
  }

  const lineTotal = menuItem.Price * item.quantity;
  total += lineTotal;

  breakdown.push({
    name: menuItem["Item Name"],
    quantity: item.quantity,
    unitPrice: menuItem.Price,
    lineTotal
  });
}

return [{
  json: {
    status: true,
    total,
    items: breakdown
  }
}];

const input = $('Geocode Address').first().json;
const response = input;
const lat = $input.first().json.restaurantLat;
const lng = $input.first().json.restaurantLng;
const maxDistance = $input.first().json.maxServingDistance;

// Check if address was found
if (response.status !== "OK" || !response.results || response.results.length === 0) {
  return [{
    json: {
      valid: false,
      error: "Address not found",
      message: "Could not find that address. Please provide more details."
    }
  }];
}

const result = response.results[0];
const location = result.geometry.location;

// Restaurant location (replace with your actual coordinates)
const RESTAURANT_LAT = lat;  // Kyiv center example
const RESTAURANT_LNG = lng;

// Calculate distance using Haversine formula
function calculateDistance(lat1, lon1, lat2, lon2) {
  const R = 6371; // Earth radius in km
  const dLat = (lat2 - lat1) * Math.PI / 180;
  const dLon = (lon2 - lon1) * Math.PI / 180;
  const a = Math.sin(dLat/2) * Math.sin(dLat/2) +
            Math.cos(lat1 * Math.PI / 180) * Math.cos(lat2 * Math.PI / 180) *
            Math.sin(dLon/2) * Math.sin(dLon/2);
  const c = 2 * Math.atan2(Math.sqrt(a), Math.sqrt(1-a));
  return R * c;
}

const distance = calculateDistance(
  RESTAURANT_LAT,
  RESTAURANT_LNG,
  location.lat,
  location.lng
);

// Delivery range limit (e.g., 5km)
const MAX_DELIVERY_DISTANCE = maxDistance;
const withinRange = distance <= MAX_DELIVERY_DISTANCE;

return [{
  json: {
    valid: true,
    withinRange: withinRange,
    formattedAddress: result.formatted_address,
    distance: Math.round(distance * 10) / 10, // Round to 1 decimal
    coordinates: {
      lat: location.lat,
      lng: location.lng
    },
    message: withinRange 
      ? `Address confirmed: ${result.formatted_address} (${Math.round(distance * 10) / 10}km away)`
      : `Sorry, ${result.formatted_address} is ${Math.round(distance * 10) / 10}km away. We only deliver within ${MAX_DELIVERY_DISTANCE}km.`
  }
}];

