function extractJsonObject(text) {
  if (!text || typeof text !== 'string') return null;

  // Remove ```json ... ``` fences
  let cleaned = text
    .replace(/```json/gi, '```')
    .replace(/```/g, '')
    .trim();

  // Try direct parse
  try {
    return { obj: JSON.parse(cleaned), cleaned };
  } catch (e) {}

  // Fallback: extract first {...} block
  const match = cleaned.match(/\{[\s\S]*\}/);
  if (!match) return null;

  try {
    return { obj: JSON.parse(match[0]), cleaned: match[0] };
  } catch (e) {
    return null;
  }
}

const aiText = $json?.content?.parts?.[0]?.text ?? '';
const parsed = extractJsonObject(aiText);

if (!parsed) {
  throw new Error(`Gemini output is not valid JSON. Raw:\n${aiText}`);
}

const data = parsed.obj;

// Basic validation
if (!Array.isArray(data.expenses)) data.expenses = [];
if (typeof data.summary_text !== 'string') data.summary_text = '';

// Context: Prioritize using the current item (to be used for Text + Photo + Voice)
const updateIdFromTrigger = $node["Telegram Trigger"]?.json?.update_id;

const ctx = {
  update_id: $json.update_id ?? updateIdFromTrigger ?? null,
  raw_input: $json.raw_input ?? null,
  message_id: $json.message_id ?? null,
  chat_id: $json.chat_id ?? null,
  source_type: $json.source_type ?? null,
  // It supports both the "now" field (in case of accidentally setting the wrong key with a space) and the $now field.
  now: $json.now ?? $json["now "] ?? $now,
};

return [{
  json: {
    ...data,
    _ai_raw_text: aiText,
    _ai_clean_json_text: parsed.cleaned,
    ...ctx,
  }
}];

// Get the configuration from the User Settings node.
const config = $('CONFIG - User Settings').item.json;
const timeZone = config.timezone || 'Asia/Ho_Chi_Minh';
const defaultCurrency = config.currency_code || 'USD';

function formatDateTimeInTZ(dateInput, tz) {
  const d = dateInput ? new Date(dateInput) : new Date();
  if (Number.isNaN(d.getTime())) return '';

  const parts = new Intl.DateTimeFormat('en-GB', {
    timeZone: tz,
    year: 'numeric', month: '2-digit', day: '2-digit',
    hour: '2-digit', minute: '2-digit', second: '2-digit',
    hour12: false,
  }).formatToParts(d);

  const map = Object.fromEntries(parts.map(p => [p.type, p.value]));
  return `${map.year}-${map.month}-${map.day} ${map.hour}:${map.minute}:${map.second}`;
}

const expenses = $json.expenses ?? [];
const updateId = $node["Telegram Trigger"].json.update_id;
const fallbackDate = formatDateTimeInTZ($json.now ?? new Date(), timeZone);

return expenses.map((e) => {
  const dateValue = (typeof e?.date === 'string' && e.date.trim())
      ? e.date.trim()
      : fallbackDate;

  return {
    json: {
      update_id: updateId,
      message_id: $json.message_id,
      chat_id: $json.chat_id,
      source_type: $json.source_type,
      raw_input: $json.raw_input,

      item: e.item ?? '',
      amount: Number(e.amount ?? 0),
      // Use the default currency from the Config if the AI doesn't return it.
      currency: e.currency ?? defaultCurrency,
      category: e.category ?? 'Other',
      payment_method: e.payment_method ?? 'Cash',

      date: dateValue,
      summary_text: $json.summary_text,
    }
  };
});

const photos = $json?.message?.photo ?? [];
if (!photos.length) throw new Error('No message.photo found');

const best = [...photos].sort((a, b) => (a.file_size ?? 0) - (b.file_size ?? 0)).pop();

return [{
  json: {
    ...$json,
    file_id: best.file_id,
    photo_width: best.width,
    photo_height: best.height,
    photo_file_size: best.file_size ?? null,
  }
}];

// 1) Get text output from Gemini Analyze Image
const aiText =
  $json?.content?.parts?.[0]?.text ??
  $json?.text ??
  '';

// 2) Get context from Set (Photo Context)
const ctx = $node["Set (Photo Context)"].json;

return [{
  json: {
    // Standardize to match the format Parse is reading.
    content: { parts: [{ text: aiText }] },

    // Context for downstream to share
    update_id: ctx.update_id,
    message_id: ctx.message_id,
    chat_id: ctx.chat_id,
    source_type: ctx.source_type ?? 'photo',

    // raw_input: prioritize raw_input if you have it, fallback caption
    raw_input: ctx.raw_input ?? ctx.caption ?? '[photo]',

    now: ctx.now ?? $now,
  }
}];

