function parseAmount(text) {
  if (!text) return null;

  // Remove command prefix
  let s = text.trim().replace(/^\/add(?:@\w+)?\s+budget\b/i, '').trim();

  // Normalize: Remove commas (1,000 -> 1000)
  s = s.replace(/,/g, '').replace(/\s+/g, ' ').trim();
  const lower = s.toLowerCase();

  // Find number
  const m = lower.match(/(\d+(?:\.\d+)?)/);
  if (!m) return null;

  const num = Number(m[1]);
  if (!Number.isFinite(num)) return null;

  let multiplier = 1;
  // Global suffixes: k = 1000, m = 1,000,000
  if (/(k|grand)\b/i.test(lower)) multiplier = 1000;
  if (/(m|mil|million)\b/i.test(lower)) multiplier = 1000000;

  // support Vietnamese currency (Hybrid)
  if (/(tr|tri·ªáu|trieu)\b/i.test(lower)) multiplier = 1000000; 

  return Math.round(num * multiplier);
}

const text = $json.message?.text ?? '';
const amount = parseAmount(text);
// Get the configuration to reformat the display for the user
const config = $('CONFIG - User Settings').item.json;
const locale = config.locale || 'en-US';
const symbol = config.currency_symbol || '$';

return [{
  json: {
    ok: !!(amount && amount > 0),
    amount: amount || 0,
    chat_id: $json.message?.chat?.id,
    message_id: $json.message?.message_id,
    raw_text: text,
    formatted_amount: (amount || 0).toLocaleString(locale) + ' ' + symbol
  }
}];

const r = $json;
const config = $('CONFIG - User Settings').item.json;
const locale = config.locale || 'en-US';
const symbol = config.currency_symbol || '$';

function num(v){ return Number(String(v ?? '0').replace(/[^\d.-]/g,'')) || 0; }
function fmt(n){ return num(n).toLocaleString(locale) + ' ' + symbol; }

const note = String(r.note ?? '').trim();

const text =
`üìä *Daily Report* (${r.date})

üí∞ Total budget: *${fmt(r.total_budget)}*
üí∏ Total spent: *${fmt(r.total_spent)}*
üè¶ Remaining: *${fmt(r.remaining)}*
üìâ Daily Avg: *${fmt(r.monthly_rate)}*${note ? `\n\nüìù Note: ${note}` : ''}`;

return [{ json: { text } }];

const chatId = $node["Telegram Trigger"].json.message?.chat?.id;
if (!chatId) throw new Error("Missing chat_id from Telegram Trigger");

const token = `${Date.now()}_${Math.random().toString(16).slice(2)}`;

return [{
  json: {
    chat_id: String(chatId),
    report_token: token,
  }
}];

const scheduledToken = $node["Wait"].json.report_token;

// Data table Get row(s): each row is an item => must be retrieved via $input.all()
const items = $input.all().map(i => i.json);

if (!items.length) {
  return [{ json: { shouldSend: false, reason: 'No row found', itemsCount: 0 } }];
}

// If the Upgrade is correct, there is usually only 1 item.
// But to be safe, use the newest one based on updatedAt (fallback is based on ID).
items.sort((a, b) => {
  const ta = new Date(a.updatedAt || a.createdAt || 0).getTime();
  const tb = new Date(b.updatedAt || b.createdAt || 0).getTime();
  if (ta !== tb) return tb - ta;
  return (b.id || 0) - (a.id || 0);
});

const latest = items[0];
const latestToken = latest?.report_token ?? null;

return [{
  json: {
    shouldSend: String(latestToken ?? '') === String(scheduledToken ?? ''),
    debug_latestToken: latestToken,
    debug_scheduledToken: scheduledToken,
    debug_itemsCount: items.length,
    debug_latestRowId: latest?.id ?? null,
  }
}];
