// Validate incoming body and ensure required fields exist
const items = $input.all();
return items.map(item => {
  const body = item.json;
  if (!body.line || !body.stop) {
    throw new Error('Request body must include "line" and "stop"');
  }
  return { json: { line: body.line, stop: body.stop } };
});

// Build status & schedule URLs for the requested line/stop
const line = $json.line;
const stop = encodeURIComponent($json.stop);
const base = `https://www.citytransitauthority.com/api/next-departures?line=${line}&stop=${stop}`;
return [{ json: { url: base, line } }];

// Flatten each departure into individual items with useful metadata
const data = $json.departures;
return data.map(dep => {
  return {
    json: {
      line: $item(0).$json.line,
      destination: dep.destination,
      departureISO: dep.departure_time,
      delayMinutes: Number(dep.delay_minutes) || 0,
      title: `${$item(0).$json.line} ‚Üí ${dep.destination} @ ${dep.departure_time}`,
      msTeamsMessage: `Line ${$item(0).$json.line} to ${dep.destination} departs at ${dep.departure_time}. Current delay: ${dep.delay_minutes} min.`
    }
  };
});
