// Aggregate and format data from all sources
const dateInfo = items[0].json;
const calendarEvents = $input.item(1).json.items || [];
const tasks = $input.item(2).json || [];
const slackMessages = $input.item(3).json.messages || [];

// Classify meetings by day
const meetingsToday = calendarEvents.filter(e => {
  const start = new Date(e.start.dateTime || e.start.date);
  return start.toDateString() === new Date(dateInfo.dateFormatted).toDateString();
});

// Categorize tasks by priority
const highPriorityTasks = tasks.filter(t => t.priority >= 3);
const normalTasks = tasks.filter(t => t.priority < 3);

// Calculate Slack usage by hour
const slackByHour = {};
slackMessages.forEach(msg => {
  const hour = new Date(msg.ts * 1000).getHours();
  slackByHour[hour] = (slackByHour[hour] || 0) + 1;
});

return {
  date: dateInfo.dateFormatted,
  weekday: new Date(dateInfo.dateFormatted).getDay(),
  meetingsToday: meetingsToday,
  meetingsTomorrow: [],
  tasks: {
    highPriority: highPriorityTasks,
    normal: normalTasks
  },
  slackUsage: {
    totalMessages: slackMessages.length,
    byHour: slackByHour
  }
};

// Calculate weekly aggregates
const weeklyData = items;

const totalMeetingHours = weeklyData.reduce((sum, d) => sum + parseFloat(d.json.meetingHours || 0), 0);
const avgMeetingHours = totalMeetingHours / weeklyData.length;
const totalSlackMessages = weeklyData.reduce((sum, d) => sum + (d.json.slackMessages || 0), 0);
const totalTasks = weeklyData.reduce((sum, d) => sum + (d.json.tasksCount || 0), 0);

return {
  weekRange: `${weeklyData[0].json.date} - ${weeklyData[weeklyData.length-1].json.date}`,
  dailyMetrics: weeklyData.map(d => d.json),
  weeklySummary: {
    totalMeetingHours: totalMeetingHours.toFixed(1),
    avgMeetingHoursPerDay: avgMeetingHours.toFixed(1),
    totalTasks: totalTasks,
    totalSlackMessages: totalSlackMessages,
    avgSlackMessagesPerDay: (totalSlackMessages / weeklyData.length).toFixed(0)
  }
};
