// Format thread messages
const messages = $input.all();
const config = $('Workflow Configuration1').item.json;
let conversation = '';
let metadata = {};

if (messages && messages.length > 0) {
  // Treat the first message as the question
  const firstMsg = messages[0].json;
  conversation = `Q: ${firstMsg.text || ''}\n`;

  metadata = {
    question_author: firstMsg.user || '',
    channel_id: firstMsg.channel || $json.channel,
    thread_ts: firstMsg.thread_ts || firstMsg.ts,
    created_at: new Date(parseFloat(firstMsg.ts) * 1000).toISOString(),
    channel_name: $json.channel_name || 'unknown',
    slack_workspace_id: config.slackWorkspaceId // Get from Config node
  };

  // Format replies as answers
  for (let i = 1; i < messages.length; i++) {
    const reply = messages[i].json;
    conversation += `A${i}: ${reply.user || 'Unknown'} - ${reply.text || ''}\n`;
  }
}

return {
  conversation_for_ai: conversation.trim(),
  ...metadata
};

// Merge OpenAI output with metadata
const aiResponse = JSON.parse($json.content || '{}');
const prevData = $input.item.json;
const config = $('Workflow Configuration1').item.json;

// Generate Slack Thread URL (Using ID from Config node)
const workspaceId = config.slackWorkspaceId || 'YOUR_WORKSPACE_ID';
const channelId = prevData.channel_id || '';
const threadTsClean = (prevData.thread_ts || '').replace('.', '');
const threadUrl = `https://app.slack.com/client/${workspaceId}/${channelId}/thread/${channelId}-${threadTsClean}`;

return {
  title: aiResponse.faq_question || 'Untitled FAQ',
  answer_markdown: aiResponse.faq_answer || '',
  tags: aiResponse.tags || [],
  summary: aiResponse.summary || '',
  slack_thread_url: threadUrl,
  channel_name: prevData.channel_name || 'unknown',
  created_at: prevData.created_at || new Date().toISOString(),
  notion_database_id: config.notionDatabaseId
};

