// This script processes a MobSF JSON report to extract key security findings for a developer report.
// It replaces the full JSON with a summarized version.

for (const item of $input.all()) {
  // The MobSF report is expected in item.json.
  // This line handles cases where the full JSON is nested.
  const mobsfReport = item.json.json || item.json;

  const report = {};

  // 1. Basic App Info for context
  report.appInfo = {
    appName: mobsfReport.app_name,
    versionName: mobsfReport.version_name,
    packageName: mobsfReport.package_name,
    md5: mobsfReport.md5,
    minSdk: mobsfReport.min_sdk,
    targetSdk: mobsfReport.target_sdk,
  };

  // 2. Manifest Analysis: Focus on high/warning severity issues
  if (mobsfReport.manifest_analysis && mobsfReport.manifest_analysis.manifest_findings) {
    const importantFindings = mobsfReport.manifest_analysis.manifest_findings.filter(finding =>
      finding.severity === 'high' || finding.severity === 'warning'
    );
    if (importantFindings.length > 0) {
        report.manifestAnalysis = {
            summary: mobsfReport.manifest_analysis.manifest_summary,
            findings: importantFindings
        };
    }
  }

  // 3. Code Analysis: Focus on Medium/High severity vulnerabilities (Corrected Logic)
  if (mobsfReport.code_analysis && mobsfReport.code_analysis.findings) {
    const importantCodeFindings = [];
    // Iterate over the keys (e.g., "insecure_data_storage", "android_logging") of the findings object
    for (const key of Object.keys(mobsfReport.code_analysis.findings)) {
        const finding = mobsfReport.code_analysis.findings[key];
        // Check if the finding has metadata and a relevant severity
        if (finding.metadata && (finding.metadata.severity === 'High' || finding.metadata.severity === 'Medium')) {
            const finding_report = { ...finding.metadata }; // create a copy
            finding_report.type = key; // Add the type of finding (e.g., "insecure_data_storage")
            finding_report.affected_files_count = Object.keys(finding.files).length;
            importantCodeFindings.push(finding_report);
        }
    }
    if (importantCodeFindings.length > 0) {
        report.codeAnalysis = {
            summary: mobsfReport.code_analysis.summary,
            findings: importantCodeFindings
        };
    }
  }

  // 4. Dangerous Permissions: List all permissions that can expose sensitive data
  if (mobsfReport.permissions) {
    const dangerousPermissions = [];
    for (const permissionName in mobsfReport.permissions) {
      const permissionDetails = mobsfReport.permissions[permissionName];
      if (permissionDetails.status === 'dangerous') {
        dangerousPermissions.push({
          permission: permissionName,
          info: permissionDetails.info,
          description: permissionDetails.description
        });
      }
    }
    if (dangerousPermissions.length > 0) {
        report.dangerousPermissions = dangerousPermissions;
    }
  }

  // 5. Security Highlights: Include good news and other points of interest
  report.securityHighlights = {};

  // Network Security Summary
  if (mobsfReport.network_security) {
      if (mobsfReport.network_security.network_findings && mobsfReport.network_security.network_findings.length === 0) {
          report.securityHighlights.network = "OK: No network security issues found.";
      } else {
          report.securityHighlights.network = mobsfReport.network_security.network_findings;
      }
  }

  // Firebase Database Summary
  if (mobsfReport.firebase) {
      if (mobsfReport.firebase.urls && mobsfReport.firebase.urls.length === 0) {
          report.securityHighlights.firebase = "OK: No insecure Firebase databases found.";
      } else {
          report.securityHighlights.firebase = {
              message: "Potential insecure Firebase databases found. Please review.",
              urls: mobsfReport.firebase.urls
          };
      }
  }

  // Trackers Summary
  if (mobsfReport.trackers && mobsfReport.trackers.trackers && mobsfReport.trackers.trackers.length > 0) {
      report.trackers = {
          count: mobsfReport.trackers.trackers.length,
          // Just list the names for brevity in the summary
          detected: mobsfReport.trackers.trackers.map(t => t.name)
      };
  }

  // Overwrite the original json with the new, summarized report
  item.json = report;
}

// Return the modified items
return $input.all();

// Get the actual HTML text from your JSON structure
let html = $json["output"][0]["content"][0]["text"];

// 1. Remove newlines and tabs
html = html.replace(/\n/g, "").replace(/\t/g, "");

// 2. Remove backslashes before quotes (if any)
html = html.replace(/\\"/g, '"');

// 3. Remove double backslashes (if any)
html = html.replace(/\\\\/g, "\\");

// 4. Trim unnecessary spaces
html = html.trim();

return {
  cleaned_html: html
};
