// Flatten the result array
const allLogs = $json.result || [];

return allLogs
  .map(logs => {
    if (!logs.topics || logs.topics.length < 3) {
      return null;  // skip invalid logs
    }

    function hexToAddress(topic) { return "0x" + topic.slice(-40); }
    function hexToDecimal(hex) { return BigInt(hex).toString(); }

    return {
      json: {
        contract: logs.address,
        from: hexToAddress(logs.topics[1]),
        to: hexToAddress(logs.topics[2]),
        value: hexToDecimal(logs.data),
        blockNumber: logs.blockNumber,
        txHash: logs.transactionHash
      }
    };
  })
  .filter(Boolean);  // remove skipped logs

// Filter items that have a valid Value in Airtable output
const validItems = items.filter(item => item.json.fields && item.json.fields.Value !== undefined);

if (validItems.length === 0) {
  return [{
    json: { text: "No high-value transfers found." }
  }];
}

// Find the largest transfer
const largest = validItems.reduce((max, item) => {
  return BigInt(item.json.fields.Value) > BigInt(max.json.fields.Value) ? item : max;
}, validItems[0]);

return [{
  json: {
    text: `${validItems.length} high-value transfers detected. Largest transfer: ${largest.json.fields.Value} from ${largest.json.fields["From Address"]} to ${largest.json.fields["To Address"]}. Stored in Airtable.`
  }
}];
