// Import crypto module for SHA-256 hashing
const crypto = require('crypto');

// Get input data from the form
const propertyId = $input.item.json.propertyId;
const ownerName = $input.item.json.ownerName;
const ownerAddress = $input.item.json.ownerAddress;
const propertyValue = $input.item.json.propertyValue;
const location = $input.item.json.location;
const legalDescription = $input.item.json.legalDescription;
const transactionType = $input.item.json.transactionType;

// Generate timestamp
const timestamp = new Date().toISOString();

// Create data string for hashing
const dataToHash = JSON.stringify({
  propertyId,
  ownerName,
  ownerAddress,
  propertyValue,
  location,
  legalDescription,
  transactionType,
  timestamp
});

// Generate SHA-256 hash
const propertyHash = crypto.createHash('sha256').update(dataToHash).digest('hex');

// Return original data plus hash and timestamp
return {
  ...($input.item.json),
  propertyHash,
  blockchainTimestamp: timestamp
};

// Import crypto module for SHA-256 hashing
const crypto = require('crypto');

// Get input data from the form
const propertyId = $input.item.json.propertyId || '';
const ownerName = $input.item.json.ownerName || '';
const ownerAddress = $input.item.json.ownerAddress || '';
const location = $input.item.json.location || '';
const additionalDetails = $input.item.json.additionalDetails || ''; // lease terms
const transactionType = $input.item.json.transactionType || '';

// Generate timestamp
const timestamp = new Date().toISOString();

// Create data string for hashing
const dataToHash = `${propertyId}|${ownerName}|${ownerAddress}|${location}|${additionalDetails}|${transactionType}|${timestamp}`;

// Generate SHA-256 hash
const leaseHash = crypto.createHash('sha256').update(dataToHash).digest('hex');

// Return original data plus generated hash and timestamp
return {
  ...($input.item.json),
  leaseHash: leaseHash,
  blockchainTimestamp: timestamp
};

