parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "tenantId": {
      "type": "string",
      "description": "Unique tenant identifier"
    },
    "tenantName": {
      "type": "string",
      "description": "Tenant name"
    },
    "satisfactionScore": {
      "type": "number",
      "description": "Overall satisfaction score from 0-100"
    },
    "churnRiskScore": {
      "type": "number",
      "description": "Churn risk score from 0-100, higher means more likely to leave"
    },
    "churnRiskLevel": {
      "type": "string",
      "description": "Risk level: low, medium, or high"
    },
    "keyIssues": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "List of key issues affecting tenant satisfaction"
    },
    "recommendedActions": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Recommended retention actions"
    },
    "engagementLevel": {
      "type": "string",
      "description": "Engagement level: low, medium, or high"
    }
  }
}

