// Aggregate all news items from the three sources
const allNews = [];

// Helper function to extract text from XML-like content
function extractText(content, tag) {
  const regex = new RegExp(`<${tag}[^>]*>([\\s\\S]*?)</${tag}>`, 'i');
  const match = content.match(regex);
  if (match) {
    // Remove CDATA wrapper if present
    let text = match[1].replace(/<!\[CDATA\[([\s\S]*?)\]\]>/g, '$1');
    // Remove HTML tags
    text = text.replace(/<[^>]+>/g, '');
    return text.trim();
  }
  return '';
}

// Helper function to extract link from RSS item
function extractLink(itemContent) {
  // Try standard link tag
  let link = extractText(itemContent, 'link');
  if (link) return link;

  // Try guid if it looks like a URL
  let guid = extractText(itemContent, 'guid');
  if (guid && guid.startsWith('http')) return guid;

  return '';
}

// Collect items from all input sources
for (const item of $input.all()) {
  const data = item.json;

  // Skip error responses
  if (!data || data.error) continue;

  // Check if it's RSS/XML format (string response)
  if (typeof data === 'string' || data.data) {
    const xmlContent = typeof data === 'string' ? data : data.data;
    if (typeof xmlContent === 'string' && xmlContent.includes('<item>')) {
      // Parse RSS items
      const itemRegex = /<item>([\s\S]*?)<\/item>/gi;
      let match;
      while ((match = itemRegex.exec(xmlContent)) !== null) {
        const itemContent = match[1];
        const title = extractText(itemContent, 'title');
        const description = extractText(itemContent, 'description') || extractText(itemContent, 'content:encoded');
        const link = extractLink(itemContent);
        const pubDate = extractText(itemContent, 'pubDate');
        const source = extractText(itemContent, 'source') || 'RSS Feed';

        if (title) {
          allNews.push({
            title: title,
            description: description.substring(0, 300) + (description.length > 300 ? '...' : ''),
            link: link,
            source: source,
            publishedAt: pubDate || new Date().toISOString()
          });
        }
      }
    }
  }
  // Check if it's JSON API format (NewsAPI style)
  else if (data.articles && Array.isArray(data.articles)) {
    data.articles.forEach(article => {
      allNews.push({
        title: article.title || '',
        description: (article.description || article.summary || '').substring(0, 300),
        link: article.url || article.link || '',
        source: article.source?.name || article.source || 'Unknown',
        publishedAt: article.publishedAt || article.pubDate || new Date().toISOString()
      });
    });
  }
  // Check if it's a single article object
  else if (data.title) {
    allNews.push({
      title: data.title || '',
      description: (data.description || data.summary || '').substring(0, 300),
      link: data.url || data.link || '',
      source: data.source?.name || data.source || 'Unknown',
      publishedAt: data.publishedAt || data.pubDate || new Date().toISOString()
    });
  }
}

// Remove duplicates based on title
const seen = new Set();
const uniqueNews = allNews.filter(item => {
  const key = item.title.toLowerCase().trim();
  if (seen.has(key)) return false;
  seen.add(key);
  return true;
});

// Rank by recency (most recent first)
uniqueNews.sort((a, b) => {
  const dateA = new Date(a.publishedAt);
  const dateB = new Date(b.publishedAt);
  return dateB - dateA;
});

// Get top 5 stories
const top5News = uniqueNews.slice(0, 5);

// Return the top 5 stories
return top5News.map(article => ({
  json: {
    title: article.title,
    description: article.description,
    link: article.link,
    source: article.source,
    publishedAt: article.publishedAt
  }
}));

