options.customCss:
:root {
	--font-family: 'Inter', 'Open Sans', system-ui, sans-serif;
	--font-weight-normal: 400;
	--font-weight-bold: 600;

	--font-size-body: 12px;
	--font-size-label: 14px;
	--font-size-test-notice: 12px;
	--font-size-input: 14px;
	--font-size-header: 20px;
	--font-size-paragraph: 14px;
	--font-size-link: 12px;
	--font-size-error: 12px;

	--font-size-html-h1: 28px;
	--font-size-html-h2: 20px;
	--font-size-html-h3: 16px;
	--font-size-html-h4: 14px;
	--font-size-html-h5: 12px;
	--font-size-html-h6: 10px;
	--font-size-subheader: 14px;

	/* Colors ‚Äî Pastel Red / White / Black */
	--color-background: #0c0c10;

	--color-test-notice-text: #e57373;
	--color-test-notice-bg: rgba(229, 115, 115, 0.12);
	--color-test-notice-border: rgba(229, 115, 115, 0.35);

	--color-card-bg: #15151a;
	--color-card-border: rgba(255, 255, 255, 0.08);
	--color-card-shadow: rgba(0, 0, 0, 0.6);

	--color-link: #ffffff;
	--color-header: #ffffff;
	--color-header-subtext: #b6b6c2;
	--color-label: #d4d4dc;

	--color-input-border: rgba(255, 255, 255, 0.14);
    --color-input-text: #000000;
	--color-focus-border: #e57373;

	--color-submit-btn-bg: #e57373;
	--color-submit-btn-text: #ffffff;

	--color-error: #ef5350;
	--color-required: #e57373;

	--color-clear-button-bg: rgba(255, 255, 255, 0.12);

	--color-html-text: #e2e2ea;
	--color-html-link: #e57373;

	/* Border Radii */
	--border-radius-card: 12px;
	--border-radius-input: 10px;
	--border-radius-clear-btn: 50%;
	--card-border-radius: 12px;

	/* Spacing */
	--padding-container-top: 28px;
	--padding-card: 28px;
	--padding-test-notice-vertical: 12px;
	--padding-test-notice-horizontal: 24px;
	--margin-bottom-card: 18px;
	--padding-form-input: 14px;
	--card-padding: 28px;
	--card-margin-bottom: 18px;

	/* Dimensions */
	--container-width: 448px;
	--submit-btn-height: 50px;
	--checkbox-size: 18px;

	/* Others */
	--box-shadow-card: 
		0px 10px 28px rgba(0, 0, 0, 0.65),
		0 0 0 1px rgba(255, 255, 255, 0.04);

	--opacity-placeholder: 0.45;
}
