// Input
const text =
  $json?.text ??
  $json?.data?.text ??
  $input.first().json?.text ??
  "";

// 1) Normalize text
const normalize = (s) =>
  String(s ?? "")
    .trim()
    .replace(/[„Éª‚óè‚ñ†‚óÜ‚óá‚ñ∂Ô∏é]/g, "")        
    .replace(/[„ÄÄ\t]+/g, " ")           
    .replace(/[Ôºö]/g, ":")              
    .replace(/[Ôºà(].*?[Ôºâ)]/g, "")       
    .trim();

const normKey = (k) =>
  normalize(k)
    .toLowerCase()
    .replace(/\s+/g, "");

// 2) Key aliases mapping
const KEY_ALIASES = {
  company: new Set([
    "company name", "company", "organization", "corporation",
    "‰ºöÁ§æÂêç","Ê≥ï‰∫∫Âêç","‰ºÅÊ•≠Âêç","Á§æÂêç","‰ºöÁ§æ","Ê≥ï‰∫∫","‰ºÅÊ•≠"
  ]),
  name: new Set([
    "contact person", "name", "person", "full name",
    "ÊãÖÂΩìËÄÖÂêç","Ê∞èÂêç","ÂêçÂâç","ÊãÖÂΩìËÄÖ"
  ]),
  department: new Set([
    "department", "division", "section",
    "ÊãÖÂΩìËÄÖÂêç„ÅÆÊâÄÂ±ûÈÉ®ÁΩ≤Âêç","ÊâÄÂ±ûÈÉ®ÁΩ≤","ÈÉ®ÁΩ≤","ÈÉ®ÈñÄ","ÊâÄÂ±û"
  ]),
  address: new Set([
    "address", "location",
    "‰ΩèÊâÄ","ÊâÄÂú®Âú∞"
  ]),
  email: new Set([
    "email", "e-mail", "mail",
    "„É°„Éº„É´„Ç¢„Éâ„É¨„Çπ","e„É°„Éº„É´","ÈÄ£Áµ°ÂÖà„É°„Éº„É´"
  ]),
  phone: new Set([
    "phone", "telephone", "tel", "mobile", "cell",
    "ÈõªË©±Áï™Âè∑","Êê∫Â∏Ø","ÈÄ£Áµ°ÂÖà","ÈõªË©±"
  ]),
};

// 3) Parse lines
const toLines = (t) =>
  String(t)
    .replace(/\\n/g, "\n")
    .split(/\r?\n/)
    .map((l) => normalize(l))
    .filter(Boolean);

const lines = toLines(text);

// 4) Resolve key
function resolveKey(rawKey) {
  const nk = normKey(rawKey);

  for (const [fixed, set] of Object.entries(KEY_ALIASES)) {
    for (const alias of set) {
      if (nk === normKey(alias)) return fixed;
    }
  }

  if (nk.includes("tel") || nk.includes("phone") || nk.includes("mobile") || nk.includes("Êê∫Â∏Ø")) return "phone";
  if (nk.includes("mail")) return "email";
  if (nk.includes("‰ΩèÊâÄ") || nk.includes("ÊâÄÂú®Âú∞") || nk.includes("address")) return "address";
  if (nk.includes("‰ºöÁ§æ") || nk.includes("Á§æÂêç") || nk.includes("company")) return "company";
  if (nk.includes("ÈÉ®ÁΩ≤") || nk.includes("department") || nk.includes("division")) return "department";
  if (nk.includes("ÊãÖÂΩì") || nk.includes("Ê∞èÂêç") || nk.includes("name")) return "name";

  return null;
}

// 5) Extract phone numbers
function extractPhones(s) {
  const str = String(s ?? "");
  const matches = str.match(/\b0\d{1,4}[-\s]?\d{1,4}[-\s]?\d{3,4}\b/g) ?? [];
  const cleaned = matches
    .map((p) => p.replace(/\s+/g, "-"))
    .map((p) => p.replace(/-+/g, "-"))
    .map((p) => p.trim())
    .filter(Boolean);

  return Array.from(new Set(cleaned));
}

const out = {
  company: "",
  name: "",
  department: "",
  address: "",
  email: "",
  phone: "",
  phoneList: [],
};

for (const line of lines) {
  const m = line.match(/^(.+?):\s*(.+)$/);
  if (!m) continue;

  const rawKey = m[1];
  const value = m[2];

  const fixed = resolveKey(rawKey);
  if (!fixed) continue;

  if (out[fixed]) continue;

  out[fixed] = value;
}

const phones = extractPhones(out.phone);
out.phoneList = phones;

if (phones.length) out.phone = phones.join(" / ");

if (out.email) out.email = String(out.email).trim().toLowerCase();

for (const k of Object.keys(out)) {
  if (typeof out[k] === "string" && out[k].trim() === "") delete out[k];
  if (Array.isArray(out[k]) && out[k].length === 0) delete out[k];
}

return [{ 
  json: out,
  binary: $input.first().binary
}];

