// 1. Current execution time (same as schedule time)
const now = new Date();

// 2. Calculate 24 hours ago
const cutoff = new Date(now.getTime() - 24 * 60 * 60 * 1000);

// 3. Orders coming from previous node
const orders = items;

// 4. Filter orders from last 24 hours
const last24hOrders = orders.filter(order => {
  const created = new Date(order.json.date_created);
  return created >= cutoff;
});

return last24hOrders;

let totalRevenue = 0;
let currency = null;
let orderCount = items.length;

for (const item of items) {
  const order = item.json;

  // Convert string ‚Üí number
  const orderTotal = parseFloat(order.total || 0);
  totalRevenue += orderTotal;

  // Capture currency once
  if (!currency) {
    currency = order.currency;
  }
}

// NEW: Average Order Value
const avgOrderValue =
  orderCount > 0 ? totalRevenue / orderCount : 0;

return [
  {
    json: {
      revenue_last_24h: Number(totalRevenue.toFixed(2)),
      currency: currency,
      order_count: orderCount,
      avg_order_value: Number(avgOrderValue.toFixed(2))
    }
  }
];

