// List of regulatory news pages
const urls = [
  { url: 'https://www.sec.gov/news/pressreleases', source: 'SEC Press Releases' },
  { url: 'https://www.fca.org.uk/news', source: 'UK FCA News' },
  { url: 'https://www.esma.europa.eu/press-news/esma-news', source: 'ESMA News' }
];
return urls.map(item => ({ json: item }));

// Normalise, tag and add IDs
const items = $input.all();
const keyword = /(rule|regulation|directive|act)/i;
return items.flatMap(item => {
  const dataArray = Array.isArray(item.json) ? item.json : [item.json];
  return dataArray.map(d => {
    const title = d.title || d.headline || '';
    const summary = d.summary || d.description || '';
    const id = Buffer.from((d.url || title).toString()).toString('base64').slice(0,24);
    return {
      json: {
        ...d,
        dedupId: id,
        isImportant: keyword.test(title) || keyword.test(summary),
        scrapedAt: new Date().toISOString(),
        source: d.sourceName || item.json.source || 'unknown'
      }
    };
  });
});
