// Merge scraped data with enrichment service response
const scraped = items[0].json;
const enrichment = scraped && scraped.metadata ? scraped.metadata : {};
return [
  {
    json: {
      title: scraped.title,
      summary: scraped.summary,
      publication_date: scraped.publication_date,
      reference_number: scraped.reference_number || '',
      source_url: scraped.source_url,
      topics: enrichment.topics || [],
      sentiment: enrichment.sentiment || 'neutral',
      source: $json.source,
      original_url: $json.url
    }
  }
];

// Generate a lightweight unique identifier for deduplication
const crypto = require('crypto');
const item = $input.item.json;
const idString = `${item.title}-${item.publication_date}`;
item.policy_id = crypto.createHash('md5').update(idString).digest('hex');
// Flag as recent if within last 30 days
const days30 = 30 * 24 * 60 * 60 * 1000;
item.is_recent = (new Date() - new Date(item.publication_date)) < days30;
return [{ json: item }];
