// CONFIG - Edit these values for your campaign
const CONFIG = {
  // Company info
  companyName: 'Your Company',
  senderEmail: 'user@example.com',
  primaryColor: '#667eea',
  secondaryColor: '#764ba2',
  logoUrl: '', // Optional: Your logo URL

  // Send settings
  batchSize: 10, // Emails per batch
  delayBetweenEmails: 2, // Seconds between emails
  delayBetweenBatches: 10, // Seconds between batches
  maxRetries: 3, // Retries per failed email

  // Current campaign
  campaignName: 'December 2024 Newsletter',
  emailSubject: 'December Updates - {{name}}',
  alternativeSubject: 'Something special for you, {{name}}', // For A/B testing
  enableABTest: true, // 50% receives each subject

  // Segmentation (filter by 'segment' column in Sheet)
  targetSegment: '', // Empty for all, or 'premium', 'new', etc.

  // Tracking
  trackOpens: true,
  trackClicks: true
};

// Generate unique campaign ID
const campaignId = `CAMP-${Date.now()}-${Math.random().toString(36).substr(2, 6).toUpperCase()}`;

return {
  json: {
    ...CONFIG,
    campaignId: campaignId,
    startedAt: new Date().toISOString(),
    startedBy: $input.item.json.body?.user || 'manual'
  }
};

const config = $('Configure Campaign').first().json;
const contacts = $input.all();

// Email validation regex
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

const results = {
  valid: [],
  invalid: [],
  alreadySent: [],
  unsubscribed: [],
  otherSegment: []
};

for (const item of contacts) {
  const c = item.json;
  const email = (c.email || '').trim().toLowerCase();
  const sent = (c.sent || '').toLowerCase();
  const unsub = (c.unsubscribed || '').toLowerCase();
  const segment = (c.segment || '').toLowerCase();

  // Validations
  if (!emailRegex.test(email)) {
    results.invalid.push({ ...c, reason: 'Invalid email' });
  } else if (sent === 'yes' || sent === 'true') {
    results.alreadySent.push(c);
  } else if (unsub === 'yes' || unsub === 'true') {
    results.unsubscribed.push(c);
  } else if (config.targetSegment && segment !== config.targetSegment.toLowerCase()) {
    results.otherSegment.push(c);
  } else {
    // Assign A/B variant
    const variant = config.enableABTest ? (Math.random() < 0.5 ? 'A' : 'B') : 'A';
    results.valid.push({
      ...c,
      email: email,
      abVariant: variant,
      subject: variant === 'A' ? config.emailSubject : config.alternativeSubject
    });
  }
}

// Return stats and valid contacts
return {
  json: {
    campaignId: config.campaignId,
    stats: {
      totalContacts: contacts.length,
      valid: results.valid.length,
      invalid: results.invalid.length,
      alreadySent: results.alreadySent.length,
      unsubscribed: results.unsubscribed.length,
      otherSegment: results.otherSegment.length,
      variantA: results.valid.filter(c => c.abVariant === 'A').length,
      variantB: results.valid.filter(c => c.abVariant === 'B').length
    },
    validContacts: results.valid,
    invalidContacts: results.invalid
  }
};

// Runs when there are no valid contacts
const stats = $('Filter and Validate').first().json.stats;

return {
  json: {
    message: 'No valid contacts to send',
    stats: stats
  }
};

const config = $('Configure Campaign').first().json;
const initialStats = $('Filter and Validate').first().json.stats;

// Calculate duration
const start = new Date(config.startedAt);
const end = new Date();
const durationMs = end - start;
const durationMin = Math.round(durationMs / 60000);

return {
  json: {
    campaignId: config.campaignId,
    campaignName: config.campaignName,

    // Times
    startedAt: config.startedAt,
    finishedAt: end.toISOString(),
    duration: durationMin < 1 ? 'Less than 1 minute' : `${durationMin} minutes`,

    // Statistics
    totalProcessed: initialStats.valid,
    variantA: initialStats.variantA,
    variantB: initialStats.variantB,

    // Summary text
    summary: `Campaign "${config.campaignName}" completed. ${initialStats.valid} emails processed in ${durationMin < 1 ? '<1' : durationMin} min.`
  }
};

const error = $input.item.json;
return {
  json: {
    errorTime: new Date().toISOString(),
    errorMessage: error.message || 'Unknown error',
    errorNode: error.node?.name || 'Unknown',
    workflow: 'Email Marketing PRO'
  }
};
