const items = $input.all();

// Thresholds
const MIN_CTR = 2;   // %
const MIN_ROAS = 4; // ratio

return items.map(({ json: item }) => {

  const impressions = Number(item.impressions || 0);
  const clicks = Number(item.clicks || 0);
  const spend = Number(item.spend || 0);

  // Current CTR
  let ctr = 0;
  if (impressions > 0) {
    ctr = (clicks / impressions) * 100;
  }

  // Revenue
  let revenue = 0;
  if (Array.isArray(item.action_values)) {
    const purchase = item.action_values.find(
      a => a.action_type === 'purchase'
    );
    if (purchase?.value) {
      revenue = Number(purchase.value);
    }
  }

  // Current ROAS
  let roas = 0;
  if (spend > 0) {
    roas = revenue / spend;
  }

  // Previous values (will be populated from Google Sheets lookup)
  const previous_ctr = item.previous_ctr || null;
  const previous_roas = item.previous_roas || null;

  // Drop logic
  let alert = false;
  let dropReason = '';
  let performanceStatus = 'OK';

  if (ctr < MIN_CTR) {
    alert = true;
    performanceStatus = 'DROPPED';
    dropReason = 'CTR below threshold';
  }

  if (roas < MIN_ROAS) {
    alert = true;
    performanceStatus = 'DROPPED';
    dropReason = dropReason
      ? dropReason + ' & ROAS below threshold'
      : 'ROAS below threshold';
  }

  return {
    json: {
      ...item,
      ctr: Number(ctr.toFixed(2)),
      revenue: Number(revenue.toFixed(2)),
      roas: Number(roas.toFixed(2)),
      previous_ctr: previous_ctr,
      previous_roas: previous_roas,
      alert,
      Performance_Status: performanceStatus,
      Drop_Reason: dropReason
    }
  };
});

