// Original UTC date
const utcDate = new Date($json.body.submitted_at.trim());

// Convert UTC → IST (+5:30)
const istOffsetMinutes = 330; // 5 * 60 + 30
const istDate = new Date(utcDate.getTime() + istOffsetMinutes * 60 * 1000);

// Extract IST values
const dayOfWeek = istDate.getDay();
const hours = istDate.getHours();
const minutes = istDate.getMinutes();

// HH:MM format
const timeHHMM =
  String(hours).padStart(2, '0') +
  ':' +
  String(minutes).padStart(2, '0');

// Total minutes since midnight (IST)
const totalMinutes = hours * 60 + minutes;

// Working hours boundaries (IST)
const startMinutes = 9 * 60 + 30;   // 09:30
const endMinutes = 18 * 60 + 30;    // 18:30

const isWorkingDay = dayOfWeek >= 1 && dayOfWeek <= 5;
const isWorkingHours =
  isWorkingDay &&
  totalMinutes >= startMinutes &&
  totalMinutes < endMinutes;

return [{
  json: {
    ...$json,

    // IST-based values
    ist_dayOfWeek: dayOfWeek,
    ist_timeHHMM: timeHHMM,
    ist_totalMinutes: totalMinutes,
    isWorkingDay,
    is_working_hours: isWorkingHours
  }
}];

const body = {
  model: "llama-3.3-70b-versatile",
  messages: [
    {
      role: "system",
      content: "You are a professional SDR assistant. You respond like a helpful human, not an AI. Keep replies short, friendly, and clear."
    },
    {
      role: "user",
      content: `SOURCE CHANNEL: ${$input.item.json.body.source_channel}
WORKING HOURS: ${$input.item.json.is_working_hours}
LEAD NAME: ${$input.item.json.body.lead_name}
LEAD MESSAGE: ${$input.item.json.body.message_text}

RULES:
- If SOURCE CHANNEL is website, write a professional business email.
- Otherwise, write a short chat message.
- If WORKING HOURS is false, mention office hours (9:30 AM–6:30 PM IST).
- Do not mention AI.
- Return only the final response text.`
    }
  ],
  temperature: 0.6,
  max_tokens: 160
};

return {
  json: {
    ...($input.item.json),
    requestBody: body
  }
};

// Get the AI response from current node (HTTP Request)
const aiResponse = $input.item.json.choices[0].message.content;

// Get original data from the "Code in JavaScript" node
const originalData = $('Code in JavaScript3').item.json;

// Merge everything together
return {
  json: {
    body: originalData.body,
    ai_response: aiResponse,
    is_working_hours: originalData.is_working_hours,
    ist_dayOfWeek: originalData.ist_dayOfWeek,
    ist_timeHHMM: originalData.ist_timeHHMM,
    isWorkingDay: originalData.isWorkingDay,
    ist_totalMinutes: originalData.ist_totalMinutes
  }
};

// Get the send response from current node
const sendResponse = $input.item.json;

// Since the send nodes don't preserve the original data,
// we need to get it from an earlier node that definitely exists
// Let's try to get from the node that comes before Switch1
const beforeSwitch = $('Code in JavaScript4').item.json;

// Determine which channel was used based on the source_channel
let channelUsed = beforeSwitch.body.source_channel || 'Unknown';
let sendStatus = 'Processed';

// Check if send was successful
if (sendResponse) {
  if (sendResponse.statusCode >= 200 && sendResponse.statusCode < 300) {
    sendStatus = 'Sent Successfully';
  } else if (sendResponse.id || sendResponse.messageId || sendResponse.labelIds) {
    // Gmail successful response
    sendStatus = 'Sent Successfully';
  } else if (sendResponse.error) {
    sendStatus = 'Failed: ' + (sendResponse.error.message || 'Unknown error');
  }
}

// Return complete data for logging
return {
  json: {
    // Lead information
    timestamp: beforeSwitch.body.submitted_at,
    lead_name: beforeSwitch.body.lead_name,
    email: beforeSwitch.body.contact_email,
    phone: beforeSwitch.body.contact_phone,
    source_channel: beforeSwitch.body.source_channel,
    lead_message: beforeSwitch.body.message_text,

    // AI Response
    ai_response: beforeSwitch.ai_response,

    // Working hours context
    working_hours: beforeSwitch.is_working_hours,
    response_time_ist: beforeSwitch.ist_timeHHMM,
    day_of_week: beforeSwitch.ist_dayOfWeek,

    // Status tracking
    status: sendStatus,
    channel_used: channelUsed,
    response_sent_at: new Date().toISOString()
  }
};
