const dayOfMonth = today.getDate(); // 1 = First day of month

// Flags
const isWeekly = dayOfWeek === 1;      // Monday
const isMonthly = dayOfMonth === 1;    // 1st day of month

return [
  {
    json: {
      runType: isMonthly ? "monthly" : isWeekly ? "weekly" : "none",
      isWeekly,
      isMonthly,
      today: today.toISOString()
    }
  }
];

