const websiteData = $node["Loop Websites1"].json;
const website = websiteData.name.toLowerCase();

const filteredAds = items.filter(item => {
  const campaign =
    item.json.name ||
    item.json.campaign_name ||
    '';
  return campaign.toLowerCase().includes(website);
});

// Add website data to each filtered ad
return filteredAds.map(ad => ({
  json: {
    ...ad.json,
    websiteInfo: {
      name: websiteData.name,
      siteId: websiteData.siteId,
      campaigns: websiteData.campaigns,
      isWeekly: websiteData.isWeekly,
      isMonthly: websiteData.isMonthly
    }
  }
}));

const websiteData = $node["Loop Websites1"].json;
const website = websiteData.name.toLowerCase();

const filteredAds = items.filter(item => {
  const campaign =
    item.json.campaign ||
    item.json.campaign_name ||
    '';
  return campaign.toLowerCase().includes(website);
});

// Add website data to each filtered ad
return filteredAds.map(ad => ({
  json: {
    ...ad.json,
    websiteInfo: {
      name: websiteData.name,
      siteId: websiteData.siteId,
      campaigns: websiteData.campaigns,
      isWeekly: websiteData.isWeekly,
      isMonthly: websiteData.isMonthly
    }
  }
}));

const website = $node["Loop Websites1"].json;

// Get data from each source
const analyticsData = $node["Mock Google Analytics"].json;
const googleAdsData = $items("Filter Google Ads By Website1");
const metaAdsData = $items("Filter Meta Ads By Website1");

return [
  {
    json: {
      website: website.name,
      siteId: website.siteId,
      isWeekly: website.isWeekly,
      isMonthly: website.isMonthly,
      analytics: analyticsData,
      googleAds: googleAdsData.map(item => item.json),
      metaAds: metaAdsData.map(item => item.json)
    }
  }
];

const data = $json;

/* -------------------------
   BASIC WEBSITE INFO
-------------------------- */
const website = data.website;
const isWeekly = data.isWeekly;
const isMonthly = data.isMonthly;

/* -------------------------
   GOOGLE ANALYTICS METRICS
-------------------------- */
const visits = Number(data.analytics.sessions || 0);
const users = Number(data.analytics.users || 0);
const pageViews = Number(data.analytics.pageViews || 0);
const siteConversions = Number(data.analytics.conversions || 0);

/* -------------------------
   GOOGLE ADS CALCULATIONS
-------------------------- */
let googleSpend = 0;
let googleClicks = 0;
let googleImpressions = 0;
let googleConversions = 0;

const googleCampaignInsights = data.googleAds.map(ad => {
  const spend = (ad.costMicros || 0) / 1_000_000;
  const clicks = ad.clicks || 0;
  const impressions = ad.impressions || 0;
  const conversions = ad.conversions || 0;

  googleSpend += spend;
  googleClicks += clicks;
  googleImpressions += impressions;
  googleConversions += conversions;

  const ctr = impressions ? (clicks / impressions) * 100 : 0;
  const cpa = conversions ? spend / conversions : null;

  let insight = "Performing well";

  if (spend > 3000 && conversions < 30) {
    insight = "High spend but low conversions ‚Äì needs optimization";
  } else if (ctr < 1) {
    insight = "Low CTR ‚Äì ad creatives or targeting may be weak";
  } else if (conversions === 0) {
    insight = "No conversions ‚Äì consider pausing";
  }

  return {
    campaign: ad.name,
    spend: Number(spend.toFixed(2)),
    clicks,
    impressions,
    conversions,
    ctr: Number(ctr.toFixed(2)),
    cpa: cpa ? Number(cpa.toFixed(2)) : null,
    insight
  };
});

/* -------------------------
   META ADS CALCULATIONS
-------------------------- */
let metaSpend = 0;
let metaClicks = 0;
let metaImpressions = 0;
let metaLeads = 0;

const metaCampaignInsights = data.metaAds.map(ad => {
  const spend = ad.spend || 0;
  const clicks = ad.clicks || 0;
  const impressions = ad.impressions || 0;
  const leads = ad.leads || 0;

  metaSpend += spend;
  metaClicks += clicks;
  metaImpressions += impressions;
  metaLeads += leads;

  const ctr = impressions ? (clicks / impressions) * 100 : 0;
  const cpl = leads ? spend / leads : null;

  let insight = "Performing well";

  if (spend > 7000 && leads < 50) {
    insight = "High spend but low leads ‚Äì targeting or offer issue";
  } else if (ctr < 0.8) {
    insight = "Low CTR ‚Äì creatives may need improvement";
  } else if (leads === 0) {
    insight = "No leads ‚Äì campaign underperforming";
  }

  return {
    campaign: ad.campaign,
    spend,
    clicks,
    impressions,
    leads,
    ctr: Number(ctr.toFixed(2)),
    cpl: cpl ? Number(cpl.toFixed(2)) : null,
    insight
  };
});

/* -------------------------
   TOTAL WEBSITE KPIs
-------------------------- */
const totalSpend = googleSpend + metaSpend;
const totalConversions = googleConversions + metaLeads;

const overallCPL = totalConversions
  ? totalSpend / totalConversions
  : null;

const conversionRate = visits
  ? (siteConversions / visits) * 100
  : 0;

/* -------------------------
   FINAL OUTPUT
-------------------------- */
return [
  {
    json: {
      website,
      isWeekly,
      isMonthly,

      websiteKPIs: {
        visits,
        users,
        pageViews,
        siteConversions,
        totalSpend: Number(totalSpend.toFixed(2)),
        totalConversions,
        overallCPL: overallCPL ? Number(overallCPL.toFixed(2)) : null,
        conversionRate: Number(conversionRate.toFixed(2))
      },

      googleAdsInsights: googleCampaignInsights,
      metaAdsInsights: metaCampaignInsights
    }
  }
];

const now = new Date();

// Get flags from "check month and week" node
const flagsData = $("check month and week1").first().json;
const isWeekly = flagsData.isWeekly;
const isMonthly = flagsData.isMonthly;

return items.filter(item => {
  const createdAt = new Date(item.json.created_date);
  if (isNaN(createdAt)) return false;

  const diffDays = (now - createdAt) / (1000 * 60 * 60 * 24);

  if (isWeekly) return diffDays <= 7;
  if (isMonthly) return diffDays <= 30;

  return false;
});

const totalLeads = items.length;

// Normalize helper
const normalize = (v) => (v || '').toLowerCase();

// Status-based counts
const statusCounts = {
  new: 0,
  contacted: 0,
  qualified: 0,
  followup: 0,
  unqualified: 0,
  converted: 0,
  lost: 0
};

// Lifecycle stage counts
const lifecycleCounts = {
  lead: 0,
  mql: 0,
  sql: 0,
  opportunity: 0,
  customer: 0
};

items.forEach(item => {
  const status = normalize(item.json.status);
  const lifecycle = normalize(item.json.lifecycle_stage);

  // Status mapping
  if (status === 'new') statusCounts.new++;
  else if (status === 'contacted') statusCounts.contacted++;
  else if (status === 'qualified') statusCounts.qualified++;
  else if (status === 'follow-up' || status === 'followup') statusCounts.followup++;
  else if (status === 'unqualified') statusCounts.unqualified++;
  else if (status === 'converted') statusCounts.converted++;
  else if (status === 'lost') statusCounts.lost++;

  // Lifecycle mapping
  if (lifecycle === 'lead') lifecycleCounts.lead++;
  else if (lifecycle.includes('marketing')) lifecycleCounts.mql++;
  else if (lifecycle.includes('sales')) lifecycleCounts.sql++;
  else if (lifecycle === 'opportunity') lifecycleCounts.opportunity++;
  else if (lifecycle === 'customer') lifecycleCounts.customer++;
});

return [
  {
    json: {
      hubspotSummary: {
        totalLeads,
        statusBreakdown: statusCounts,
        lifecycleBreakdown: lifecycleCounts,
        conversionRate: totalLeads
          ? Number(((statusCounts.converted / totalLeads) * 100).toFixed(2))
          : 0
      }
    }
  }
];

// Separate website-level reports
const websites = items
  .filter(i => i.json.website)
  .map(i => i.json);

// Normalize string booleans safely
const toBool = (v) => v === true || v === "true";

// Detect run type correctly
const isMonthly = websites.some(w => toBool(w.isMonthly));
const isWeekly = websites.some(w => toBool(w.isWeekly));

// Find HubSpot summary (single object)
const hubspotItem = items.find(
  i => i.json.hubspotSummary || i.json.hubspot
);

const hubspot = hubspotItem
  ? (hubspotItem.json.hubspotSummary || hubspotItem.json.hubspot)
  : {};

// Final unified report object
return [
  {
    json: {
      reportDate: new Date().toISOString(),
      reportType: isMonthly ? "Monthly" : "Weekly",

      websites: websites.map(w => ({
        website: w.website,
        siteId: w.siteId,
        isWeekly: toBool(w.isWeekly),
        isMonthly: toBool(w.isMonthly),
        websiteKPIs: w.websiteKPIs,
        googleAdsInsights: w.googleAdsInsights,
        metaAdsInsights: w.metaAdsInsights
      })),

      hubspot
    }
  }
];

