// ---------- Find HubSpot Summary ----------
const hubspotItem = items.find(
  i => i.json.hubspotSummary || i.json.hubspot
);

const hubspotSummary = hubspotItem
  ? (hubspotItem.json.hubspotSummary || hubspotItem.json.hubspot)
  : null;

// ---------- Safety Check ----------
if (!hubspotSummary) {
  return [];
}

// ---------- Build HubSpot Report ----------
return [
  {
    json: {
      reportType: "Monthly", // default fallback
      reportDate: new Date().toISOString(),

      hubspotSummary: {
        totalLeads: hubspotSummary.totalLeads,
        conversionRate: hubspotSummary.conversionRate,
        statusBreakdown: hubspotSummary.statusBreakdown,
        lifecycleBreakdown: hubspotSummary.lifecycleBreakdown
      }
    }
  }
];

// Transform filtered HubSpot leads data for Google Sheets
const transformedData = [];

for (const item of items) {
  const lead = item.json;

  transformedData.push({
    json: {
      "Report Date": new Date().toISOString().split('T')[0],
      "Report Type": lead.is_weekly ? "Weekly" : "Monthly",
      "Lead ID": lead.lead_id,
      "First Name": lead.first_name,
      "Last Name": lead.last_name,
      "Email": lead.email,
      "Phone": lead.phone,
      "Company": lead.company,
      "Website": lead.website,
      "Source": lead.source,
      "Campaign": lead.campaign,
      "Status": lead.status,
      "Lifecycle Stage": lead.lifecycle_stage,
      "Owner": lead.owner,
      "Created Date": lead.created_date,
      "Last Activity Date": lead.last_activity_date,
      "Data Source": "HubSpot",
      "Created At": new Date().toISOString()
    }
  });
}

return transformedData;
