// ===== CONFIG =====
const ONLY_LAST_HOURS = 36;          // Filter news from the last X hours (set null/0 if not filtering)
const MAX_ITEMS = 60;    // total number of items included in context
const MAX_TOTAL_CHARS = 28000;   // Block token overshoot (optional)
const MAX_TITLE_CHARS = 160;
const MAX_DESC_CHARS = 260;

// Mix ratio by source type (RSS vs YouTube)
// Example 70/30 => MAX_ITEMS=60 => RSS ~42, YouTube ~18 (if there's a shortage, it will be automatically compensated)
const MIX_RATIO = {
  rss: 0.7,
  youtube: 0.3,
};

// Remove posts that are in the form of deals/prices/where to buy... (less clutter and less length)
const EXCLUDE_IF_TEXT_MATCH = [
  /\bwhere to buy\b/i,
  /\bdeal\b/i,
  /\bsale\b/i,
  /\bpromo code\b/i,
  /\$\d/i,
  /\bblack friday\b/i,
  /\bcyber monday\b/i,
];

// The Verge's signature phrase is often inserted.
const STRIP_PHRASES = [
  /read the full story at the verge\.?/gi,
  /read our review\.?/gi,
];

// ==================

function decodeEntities(str) {
  str = String(str ?? '');

  const map = {
    '&nbsp;': ' ',
    '&amp;': '&',
    '&quot;': '"',
    '&#39;': "'",
    '&apos;': "'",
    '&lt;': '<',
    '&gt;': '>',
    '&rsquo;': '‚Äô',
    '&lsquo;': '‚Äò',
    '&ldquo;': '‚Äú',
    '&rdquo;': '‚Äù',
    '&mdash;': '‚Äî',
    '&ndash;': '‚Äì',
    '&hellip;': '‚Ä¶',
  };

  str = str.replace(
    /&nbsp;|&amp;|&quot;|&#39;|&apos;|&lt;|&gt;|&rsquo;|&lsquo;|&ldquo;|&rdquo;|&mdash;|&ndash;|&hellip;/g,
    (m) => map[m] ?? m
  );

  // numeric entities: &#8217;  /  &#x2019;
  str = str.replace(/&#(\d+);/g, (_, n) => {
    const code = parseInt(n, 10);
    return Number.isFinite(code) ? String.fromCodePoint(code) : _;
  });
  str = str.replace(/&#x([0-9a-fA-F]+);/g, (_, h) => {
    const code = parseInt(h, 16);
    return Number.isFinite(code) ? String.fromCodePoint(code) : _;
  });

  return str;
}

function stripHtml(s) {
  return decodeEntities(String(s ?? ''))
    .replace(/<[^>]*>/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();
}

function clip(s, n) {
  s = String(s ?? '').trim();
  if (!n || s.length <= n) return s;
  return s.slice(0, n - 1).trimEnd() + '‚Ä¶';
}

function normTitle(s) {
  return stripHtml(s).toLowerCase().replace(/\s+/g, ' ').trim();
}

function inferPublication(link, feedTitle) {
  const l = String(link ?? '');
  const ft = String(feedTitle ?? '');
  if (/techcrunch\.com/i.test(l)) return 'TechCrunch';
  if (/theverge\.com/i.test(l)) return 'The Verge';
  if (/vnexpress\.net/i.test(l)) return 'VNExpress';
  if (/youtube\.com|youtu\.be/i.test(l)) return 'YouTube';
  if (/trends\.google\.com/i.test(l)) return 'Google Trends';
  if (ft) return stripHtml(ft);
  return 'RSS';
}

function inferSourceType(j) {
  // Prioritize fields set from upstream.
  if (j?.sourceType) return String(j.sourceType);

  const link = String(j?.link || j?.url || '');
  if (/youtube\.com|youtu\.be/i.test(link)) return 'youtube';
  if (/trends\.google\.com/i.test(link)) return 'trends';

  return 'rss';
}

function parseDateMaybe(v) {
  if (!v) return null;
  const d = new Date(v);
  return Number.isNaN(d.getTime()) ? null : d;
}

function matchesAny(reList, text) {
  const t = String(text ?? '');
  return reList.some((re) => re.test(t));
}

function getTs(a) {
  const d = a.publishedAt ? new Date(a.publishedAt) : null;
  const t = d && !Number.isNaN(d.getTime()) ? d.getTime() : 0;
  return t;
}

// time cutoff
const now = new Date();
const cutoff = ONLY_LAST_HOURS ? new Date(now.getTime() - ONLY_LAST_HOURS * 3600 * 1000) : null;

// main
const seen = new Set();
const candidates = [];

let droppedDup = 0;
let droppedByTime = 0;
let droppedByExclude = 0;

for (const it of $input.all()) {
  const j = it.json || {};

  const title = stripHtml(j.title || j.Title || '');
  if (!title) continue;

  const link = stripHtml(j.link || j.url || '');
  const descRaw = stripHtml(j.description || j.content || j.snippet || '');
  const author = stripHtml(j.creator || j.author || j.dcCreator || '');

  // PublishedAt can be pubDate/isoDate/publishedAt...
  const publishedAtRaw =
    j.isoDate || j.pubDate || j.publishedAt || j.publishTime || j.date || '';
  const publishedAt = parseDateMaybe(publishedAtRaw);

  if (cutoff && publishedAt && publishedAt < cutoff) {
    droppedByTime++;
    continue;
  }

  // Filter out "deals/junk" based on aggregated text.
  const haystack = `${title}\n${descRaw}\n${link}`;
  if (matchesAny(EXCLUDE_IF_TEXT_MATCH, haystack)) {
    droppedByExclude++;
    continue;
  }

  // key dedup
  const key = link ? `L:${link}` : `T:${normTitle(title)}`;
  if (seen.has(key)) {
    droppedDup++;
    continue;
  }
  seen.add(key);

  // clean desc
  let desc = descRaw;
  for (const re of STRIP_PHRASES) desc = desc.replace(re, '').trim();
  desc = clip(desc, MAX_DESC_CHARS);

  const publication = inferPublication(link, j.feedTitle);
  const sourceType = inferSourceType(j);
  const titleClipped = clip(title, MAX_TITLE_CHARS);

  candidates.push({
    title: titleClipped,
    description: desc,
    link,
    publication,
    author,
    publishedAt: publishedAt
      ? publishedAt.toISOString()
      : (publishedAtRaw ? String(publishedAtRaw) : ''),
    sourceType,
  });
}

// ===== MIX by ratio (70/30) =====

// Group by sourceType
const rssList = [];
const ytList = [];
const otherList = [];

for (const a of candidates) {
  if (a.sourceType === 'youtube') ytList.push(a);
  else if (a.sourceType === 'rss') rssList.push(a);
  else otherList.push(a);
}

// Sort each group from newest to first (this is up to you; if you want to keep the input order, remove the sort).
rssList.sort((a, b) => getTs(b) - getTs(a));
ytList.sort((a, b) => getTs(b) - getTs(a));
otherList.sort((a, b) => getTs(b) - getTs(a));

// Quotas
let rssQuota = Math.round(MAX_ITEMS * (MIX_RATIO.rss ?? 0.7));
rssQuota = Math.max(0, Math.min(MAX_ITEMS, rssQuota));
let ytQuota = MAX_ITEMS - rssQuota;

// Pick quota
let pickedRss = rssList.slice(0, rssQuota);
let pickedYt = ytList.slice(0, ytQuota);

// Fill n·∫øu 1 b√™n thi·∫øu
if (pickedYt.length < ytQuota) {
  const need = ytQuota - pickedYt.length;
  pickedRss = pickedRss.concat(rssList.slice(rssQuota, rssQuota + need));
}
if (pickedRss.length < rssQuota) {
  const need = rssQuota - pickedRss.length;
  pickedYt = pickedYt.concat(ytList.slice(ytQuota, ytQuota + need));
}

// Alternate mixing according to the 70/30 pattern (7 RSS, 3 YT).
const w = 10;
const wR = Math.max(0, Math.min(10, Math.round((MIX_RATIO.rss ?? 0.7) * w)));
const wY = w - wR;
const pattern = [
  ...Array(wR).fill('rss'),
  ...Array(wY).fill('youtube'),
];

const queueR = [...pickedRss];
const queueY = [...pickedYt];

const articles = [];
let pi = 0;

while (articles.length < MAX_ITEMS && (queueR.length || queueY.length)) {
  const want = pattern[pi % pattern.length];
  pi++;

  if (want === 'youtube' && queueY.length) articles.push(queueY.shift());
  else if (want === 'rss' && queueR.length) articles.push(queueR.shift());
  else if (queueR.length) articles.push(queueR.shift());
  else if (queueY.length) articles.push(queueY.shift());
}

// (Optional) If still missing, fill with otherList
if (articles.length < MAX_ITEMS && otherList.length) {
  articles.push(...otherList.slice(0, MAX_ITEMS - articles.length));
}

// ===== build newsContext (for Gemini) =====
let newsContext = '';
for (const a of articles) {
  // format: [Publication | Author | sourceType] Title: Desc (Link)
  // (You can remove sourceType from the head if you don't want it to be visible)
  const headParts = [a.publication];
  if (a.author) headParts.push(a.author);
  if (a.sourceType) headParts.push(a.sourceType);

  const head = `[${headParts.join(' | ')}]`;

  const line =
    `${head} ${a.title}` +
    `${a.description ? `: ${a.description}` : ''}` +
    `${a.link ? ` (${a.link})` : ''}\n`;

  if ((newsContext.length + line.length) > MAX_TOTAL_CHARS) break;
  newsContext += line;
}

return [{
  json: {
    newsContext: newsContext.trim(),
    articles,
    stats: {
      inputCount: $input.all().length,
      candidatesCount: candidates.length,
      rssCount: rssList.length,
      youtubeCount: ytList.length,
      outputCount: articles.length,
      droppedDup,
      droppedByTime,
      droppedByExclude,
      cutoffISO: cutoff ? cutoff.toISOString() : null,
      ratio: MIX_RATIO,
      quota: {
        rssQuota,
        ytQuota,
        pickedRss: pickedRss.length,
        pickedYt: pickedYt.length,
      }
    }
  }
}];

// ====== CONFIG ======
const ONLY_LAST_HOURS = 24;

// Keyword "Positive" (Must match at least one)
const INCLUDE_REGEX = [
  /\bai\b/i,
  /\bartificial intelligence\b/i,
  /\btech\b/i,
  /\bllm\b/i,
  /\bchatgpt\b/i,
  /\bgemini\b/i,
  /\bopenai\b/i,
  /\bautomation\b/i,
  /\brobotics\b/i,
  /\bmachine learning\b/i,
];

// Keyword "Negative" (If matched, exclude item)
const EXCLUDE_REGEX = [
  /\bcrypto\b/i,
  /\bbitcoin\b/i,
  /\bcoin\b/i,
  /\binvestment\b/i,
  /\bprice prediction\b/i,
  /\bxrp\b/i,
  /\bgiveaway\b/i,
  /\blive stream\b/i, // Often low quality or just looping music
];

// ======================================

const now = new Date();
const cutoff = new Date(now.getTime() - ONLY_LAST_HOURS * 60 * 60 * 1000);

function pickThumb(snippet) {
  return (
    snippet?.thumbnails?.high?.url ||
    snippet?.thumbnails?.medium?.url ||
    snippet?.thumbnails?.default?.url ||
    ''
  );
}

function matchAny(regexList, text) {
  return regexList.some((re) => re.test(text));
}

const out = [];

for (const item of $input.all()) {
  const j = item.json;

  const videoId = j?.id?.videoId || '';
  const snippet = j?.snippet || {};

  const title = snippet?.title || '';
  const description = snippet?.description || '';
  const channelTitle = snippet?.channelTitle || '';
  const publishedAt = snippet?.publishedAt || snippet?.publishTime || '';

  if (!videoId || !title) continue;

  // Time filter (if publishedAt parse is possible)
  if (publishedAt) {
    const d = new Date(publishedAt);
    if (!Number.isNaN(d.getTime()) && d < cutoff) continue;
  }

  // Content filter
  const haystack = `${title}\n${description}\n${channelTitle}`;
  if (!matchAny(INCLUDE_REGEX, haystack)) continue;
  if (matchAny(EXCLUDE_REGEX, haystack)) continue;

  out.push({
    json: {
      title,
      link: `https://www.youtube.com/watch?v=${videoId}`,
      description,
      source: `YouTube - ${channelTitle || 'Unknown'}`,
      publishedAt,
      thumbnail: pickThumb(snippet),
      channelTitle,
      videoId,
    }
  });
}

return out;

const item = $input.first().json;

// 1) Get raw text from Gemini
const raw = (item?.candidates?.[0]?.content?.parts || [])
  .map(p => p?.text ?? '')
  .join('')
  .trim();

if (!raw) throw new Error('Gemini output error: candidates[0].content.parts[].text not found');

// 2) Cut JSON objects (to avoid any extra text).
const first = raw.indexOf('{');
const last = raw.lastIndexOf('}');
if (first === -1 || last === -1 || last <= first) {
  throw new Error('Not found JSON object in output Gemini');
}
const jsonStr = raw.slice(first, last + 1);

// 3) parse
let parsed;
try {
  parsed = JSON.parse(jsonStr);
} catch (e) {
  throw new Error('JSON.parse failed. JSON string was:\n' + jsonStr);
}

// 4) CLEAN HTML FOR TELEGRAM
let telegram_body = String(parsed.telegram_body ?? '').trim();

// Fix 1: Replace the tags <br>, <br/>, <br /> with the newline character \n
telegram_body = telegram_body.replace(/<br\s*\/?>/gi, '\n');

// Fix 2: Replace the <p> tag with a blank line and </p> with a line break (as a precaution).
telegram_body = telegram_body.replace(/<p>/gi, '').replace(/<\/p>/gi, '\n');

// Fix 3: Normalize href (change ' to " if applicable)
telegram_body = telegram_body.replace(/href='([^']+)'/g, 'href="$1"');

const audio_script = String(parsed.audio_script ?? '').trim();
const title = String(parsed.title ?? '').trim();

if (!telegram_body) throw new Error('Missing telegram_body');
if (!audio_script) throw new Error('Missing audio_script');

return [{
  json: { title, telegram_body, audio_script }
}];

const item = $input.first();
if (!item.binary?.audio) {
  throw new Error('Not found binary.audio from node HTTP TTS');
}

const desiredNameFromJson = item.json.fileName || item.json.filename || item.json.audioFileName;
let desiredName = desiredNameFromJson || 'morning_briefing.mp3';

// Make sure to add the .mp3 extension.
if (!desiredName.toLowerCase().endsWith('.mp3')) desiredName += '.mp3';

// Reset metadata for the binary.
item.binary.audio.fileName = desiredName;

// mimeType: OpenAI usually returns audio/mpeg (correct). Telegram is also OK.
// But if you want "standard mp3", then audio/mpeg is still the best option.
item.binary.audio.mimeType = 'audio/mpeg';

// (Optional) some flows/nodes can read file extensions.
item.binary.audio.fileExtension = 'mp3';

return [item];

const { title, telegram_body, audio_script } = $input.first().json;

// Guard + API-based restrictions (safe)
let input = String(audio_script || '').trim();
if (!input) throw new Error('audio_script r·ªóng');

// OpenAI TTS input is limited to 4096 characters -> temporarily truncated to avoid failure.
if (input.length > 4096) {
  input = input.slice(0, 4050).trimEnd() + '...';
}

const now = new Date();
const yyyy = now.getFullYear();
const mm = String(now.getMonth() + 1).padStart(2, '0');
const dd = String(now.getDate()).padStart(2, '0');

return [{
  json: {
    title,
    telegram_body,
    fileName: `morning_briefing_${yyyy}-${mm}-${dd}.mp3`,
    tts: {
      model: 'gpt-4o-mini-tts',
      voice: 'alloy',
      input,
      instructions: 'Clear, professional English broadcast voice.',
      response_format: 'mp3',
      speed: 1.05,
    },
  }
}];
