// Extract Reference Code from XML Response
// Supports various response formats

let xml = '';

// Check different possible structures
if (typeof $input.item.json === 'string') {
  xml = $input.item.json;
} else if ($input.item.json.body) {
  xml = $input.item.json.body;
} else if ($input.item.json.data) {
  xml = $input.item.json.data;
} else {
  // Fallback: convert entire JSON to String
  xml = JSON.stringify($input.item.json);
}

// Debug output
console.log('XML Response:', xml);

// Extract Reference Code
const match = xml.match(/<ReferenceCode>(.*?)<\/ReferenceCode>/);

if (match && match[1]) {
  return [{
    json: {
      referenceCode: match[1],
      token: 'YOUR_FLEX_TOKEN',
      debug_xml: xml.substring(0, 200)
    }
  }];
} else {
  throw new Error('No Reference Code found. Response: ' + xml.substring(0, 500));
}
