const staticData = $getWorkflowStaticData('global');
const items = $input.all();
const results = [];

for (const item of items) {
  const json = item.json;

  // --- 1. SETUP VARIABLES ---
  // Now safe to access because we fixed the data flow!
  const threadId = json["Thread ID"]; 
  const sequence = parseInt(json["Sequence"] || 1);
  const did = $('BlueSky Auth').first().json.did;
  const postBody = json["Content"]; // Using the correct column name from your sheet

  // --- 2. CONSTRUCT BASIC RECORD ---
  let record = {
    text: postBody,
    createdAt: new Date().toISOString(),
    $type: "app.bsky.feed.post"
  };

  // --- 3. HANDLE IMAGES (THE FIX) ---
  // We now check if the CURRENT item's JSON has the 'blob' data.
  // This data exists only if it came through the "Combine Data" merge node.
  if (json.blob) {
    record.embed = {
      $type: "app.bsky.embed.images",
      images: [{
        alt: json["Alt Text"] || "", // Default to empty string if missing
        image: json.blob // Use the blob directly from the item's data
      }]
    };
  }

  // --- 4. HANDLE THREADING (THE FIX) ---
  // This logic now works because 'sequence' and 'threadId' are correctly retrieved.
  if (sequence === 1) {
    // New thread: reset global pointers
    staticData.currentRoot = null;
    staticData.currentParent = null;
    staticData.currentThreadId = threadId;
  } 
  else if (sequence > 1 && staticData.currentThreadId == threadId) {
    // Reply: check if we have parent/root info from previous loop iteration
    if (staticData.currentParent && staticData.currentRoot) {
      record.reply = {
        root: staticData.currentRoot,
        parent: staticData.currentParent
      };
    }
  }

  // --- 5. FINALIZE ---
  results.push({
    json: {
      repo: did,
      collection: "app.bsky.feed.post",
      record: record
    }
  });
}

return results;

const staticData = $getWorkflowStaticData('global');
// Get the response from the Create Post node
const lastPost = $('Create Post').first().json; 
const handle = $('BlueSky Auth').first().json.handle || $('BlueSky Auth').first().json.identifier; // Get username

// --- 1. EXTRACT ID ---
// The URI looks like: at://did:plc:1234/app.bsky.feed.post/3lb234...
// We need the last part ("3lb234...") to build the web link
const uriParts = lastPost.uri.split('/');
const rkey = uriParts[uriParts.length - 1];

// --- 2. BUILD WEB URL ---
// Format: https://bsky.app/profile/{handle}/post/{id}
const webUrl = `https://bsky.app/profile/${handle}/post/${rkey}`;

// --- 3. SAVE THREAD STATE (Existing Logic) ---
const newRef = {
  uri: lastPost.uri,
  cid: lastPost.cid
};

if (!staticData.currentRoot) {
  staticData.currentRoot = newRef;
}
staticData.currentParent = newRef;

// --- 4. OUTPUT DATA FOR SHEET ---
// We pass the original sheet data + our new links
return [{
  json: {
    ...$input.first().json, // Keep original sheet data
    postUri: lastPost.uri,  // The technical ID
    postLink: webUrl        // The clickable link
  }
}];
