// Create feature dataset for AI prediction
const items = [];

for (const item of $input.all()) {
  const features = {
    productId: item.json.product_id,
    historicalSales: item.json.quantity_sold || 0,
    currentStock: item.json.current_stock || 0,
    weatherScore: item.json.weather_temp || 20,
    trendScore: item.json.trend_score || 0,
    dayOfWeek: new Date().getDay(),
    campaignFlag: item.json.campaign_flag || 0,
    safetyStock: item.json.safety_stock || 10,
    leadTime: item.json.lead_time || 3
  };

  items.push({json: features});
}

return items;

// Combine prediction results with inventory info and calculate required order quantity
const items = [];

for (const item of $input.all()) {
  const forecast = item.json.forecastQuantity || 0;
  const currentStock = item.json.currentStock || 0;
  const onOrder = item.json.onOrderQuantity || 0;
  const safetyStock = item.json.safetyStock || 10;

  const totalRequired = forecast + safetyStock;
  const totalSupply = currentStock + onOrder;
  const shortageQty = Math.max(0, totalRequired - totalSupply);

  items.push({
    json: {
      ...item.json,
      totalRequired,
      totalSupply,
      shortageQuantity: shortageQty
    }
  });
}

return items;

// Extract items to order and finalize order quantity
const orderItems = [];

for (const item of $input.all()) {
  if (item.json.shortageQuantity > 0) {
    const lotSize = item.json.minOrderLot || 1;
    const baseQty = Math.ceil(item.json.shortageQuantity);
    const orderQty = Math.ceil(baseQty / lotSize) * lotSize;

    orderItems.push({
      json: {
        productId: item.json.productId,
        productName: item.json.productName || 'Unknown',
        orderQuantity: orderQty,
        category: item.json.category,
        leadTime: item.json.leadTime
      }
    });
  }
}

return orderItems;

// Logic to select optimal supplier
const quotes = $input.all();
let bestSupplier = null;
let lowestCost = Infinity;

for (const quote of quotes) {
  const unitPrice = quote.json.unitPrice || quote.json.price || 0;
  const shipping = quote.json.shippingFee || quote.json.freight || 0;
  const qty = quote.json.orderQuantity || 1;
  const totalCost = (unitPrice * qty) + shipping;
  const leadTime = quote.json.leadTime || quote.json.deliveryDays || 999;

  // Select based on minimum cost and lead time within 7 days
  if (totalCost < lowestCost && leadTime <= 7) {
    lowestCost = totalCost;
    bestSupplier = {
      supplierName: quote.json.supplierName || 'Supplier',
      unitPrice: unitPrice,
      totalCost: totalCost,
      leadTime: leadTime,
      productId: quote.json.productId,
      orderQuantity: qty
    };
  }
}

return [{json: bestSupplier}];

