const ytNodeName = "Merge (YouTube) (Mode Append)";

const ytItems = $items(ytNodeName).map(i => i.json);
const apifyItems = $input.all();

function getVideoIdFromUrl(url = "") {
  try {
    const u = new URL(url);
    if (u.hostname.includes("youtube.com")) return u.searchParams.get("v");
    if (u.hostname === "youtu.be") return u.pathname.replace("/", "") || null;
  } catch (e) {}
  return null;
}

function pickYtVideoId(yt) {
  return (
    yt?.id?.videoId ||
    yt?.videoId ||
    yt?.snippet?.resourceId?.videoId ||
    getVideoIdFromUrl(yt?.url) ||
    null
  );
}

// Build lookup from YT by videoId
const ytById = {};
for (const yt of ytItems) {
  const vid = pickYtVideoId(yt);
  if (vid) ytById[vid] = yt;
}

// Normalize Apify items, match YT by videoId (not by idx)
return apifyItems.map(({ json: ap }) => {
  const apVideoId =
    ap?.id ||
    getVideoIdFromUrl(ap?.url) ||
    null;

  const yt = apVideoId ? (ytById[apVideoId] || {}) : {};

  const transcriptOnly = (ap?.transcript_only_text || "").toString().trim();
  const fallbackDesc = (yt?.snippet?.description || yt?.description || "").toString().trim();
  const content = (transcriptOnly || fallbackDesc).slice(0, 12000);

  const title = ap?.title || yt?.snippet?.title || yt?.title || "";
  const url = ap?.url || (apVideoId ? `https://www.youtube.com/watch?v=${apVideoId}` : "");
  const publishedAt = yt?.snippet?.publishedAt || yt?.publishedAt || "";

  return {
    json: {
      type: "YT_TRANSCRIPT",
      videoId: apVideoId,
      title,
      url,
      publishedAt,
      content,
      source: "apify_best_youtube_transcripts_scraper",
      hasTranscript: Boolean(transcriptOnly),
      // debug fields (optional, can be deleted)
      _match: {
        matchedBy: "videoId",
        ytFound: Boolean(apVideoId && ytById[apVideoId]),
      }
    }
  };
});

function stripHtml(html = "") {
  return html
    .replace(/<script[\s\S]*?<\/script>/gi, "")
    .replace(/<style[\s\S]*?<\/style>/gi, "")
    .replace(/<\/?[^>]+(>|$)/g, " ")
    .replace(/\s+/g, " ")
    .trim();
}

function clip(text = "", max = 6000) {
  const t = (text || "").toString().trim();
  return t.length > max ? t.slice(0, max) + "‚Ä¶" : t;
}

function toMs(x) {
  const ms = Date.parse(x || "");
  return Number.isNaN(ms) ? 0 : ms;
}

// FIX: The Node runtime code is missing this.getWorkflowStaticData()
const staticData =
  (typeof getWorkflowStaticData === "function" && getWorkflowStaticData("global")) ||
  (typeof $getWorkflowStaticData === "function" && $getWorkflowStaticData("global"));

if (!staticData) throw new Error("Static Data API not available in this Code node runtime");

staticData.seen = staticData.seen || {};

const now = new Date();
const nowIso = now.toISOString();
const SEEN_TTL_DAYS = 7;
const TTL_MS = SEEN_TTL_DAYS * 24 * 60 * 60 * 1000;

// ===== Token control =====
const MAX_ITEMS_TOTAL = 8;           // Gemini limits purchases to a maximum of 8 items per day.
const MAX_YT_ITEMS = 2;              // Prioritize the two most recent videos
const RSS_HOT_POOL = 10;             // Top 10 hottest RSS posts
const MAX_CONTENT_RSS = 1400;        // A sharp cut to reduce the token price
const MAX_CONTENT_YT = 1800;         // transcript also cuts strongly

function isRss(json) {
  return Boolean(
    json.link &&
      (json.pubDate ||
        json.isoDate ||
        json.content ||
        json.contentSnippet ||
        json.description ||
        json["content:encoded"])
  );
}

function competitorTagFromUrl(url = "") {
  if (url.includes("techcrunch.com")) return "techcrunch";
  if (url.includes("n8n.io")) return "n8n";
  return "unknown";
}

// ‚ÄúHot score‚Äù heuristic for RSS: recency + keyword + source boost
function rssHotScore(item) {
  const ageMs = now.getTime() - toMs(item.publishedAt);
  const ageHours = ageMs > 0 ? ageMs / (1000 * 60 * 60) : 9999;

  // Recency score: 0..1 (highest new)
  const recency = Math.max(0, 1 - ageHours / 48); // trong 48h ƒë·∫ßu l√† quan tr·ªçng

  const title = (item.title || "").toLowerCase();
  const content = (item.content || "").toLowerCase();

  // Keyword weight (depending on your domain, you can adjust it further)
  const kw = [
    { re: /\b(ai|agent|llm|genai|rag)\b/i, w: 0.30 },
    { re: /\b(robot|humanoid|automation)\b/i, w: 0.25 },
    { re: /\b(hack|breach|cyber|security|ransom)\b/i, w: 0.25 },
    { re: /\b(acquire|acquisition|merge|funding|raise|valuation|ipo)\b/i, w: 0.20 },
    { re: /\b(nvidia|google|microsoft|meta|apple|openai|anthropic)\b/i, w: 0.15 },
    { re: /\b(regulation|ban|lawsuit|court|policy)\b/i, w: 0.15 },
  ];

  let kwScore = 0;
  for (const k of kw) {
    if (k.re.test(title) || k.re.test(content)) kwScore += k.w;
  }

  // Source boost: TechCrunch tends to be more "market-moving".
  const sourceBoost = item.competitorTag === "techcrunch" ? 0.10 : 0.03;

  // Penalty for the song "stale / evergreen" in the style of "how to track santa ... 2022"
  const stalePenalty = /\b(2020|2021|2022|2023)\b/i.test(title) ? 0.25 : 0;

  return recency + kwScore + sourceBoost - stalePenalty;
}

const inputItems = $input.all();

// --- Normalize each item into a common schema ---
const normalized = inputItems.map(({ json }) => {
  if (!isRss(json)) {
    const competitorTag = json.competitorTag || "unknown";
    const videoId = json.videoId || json.id;
    const url = json.url || (videoId ? `https://www.youtube.com/watch?v=${videoId}` : "");

    return {
      id: `yt:${videoId || url}`,
      competitorTag,
      sourceType: "youtube",
      title: json.title || "",
      url,
      publishedAt: json.publishedAt || "",
      content: clip(json.content || "", MAX_CONTENT_YT),
    };
  }

  const title = json.title || "";
  const url = json.link || "";
  const publishedAt = json.isoDate || json.pubDate || "";
  const rawContent =
    json["content:encoded"] ||
    json.content ||
    json.contentSnippet ||
    json.description ||
    "";

  const competitorTag = competitorTagFromUrl(url);

  return {
    id: `rss:${url || title}`,
    competitorTag,
    sourceType: "rss",
    title,
    url,
    publishedAt,
    content: clip(stripHtml(rawContent), MAX_CONTENT_RSS),
  };
});

// --- Dedup using staticData.seen with TTL ---
const fresh = [];
for (const item of normalized) {
  const key = item.id;
  const seenAt = staticData.seen[key];

  if (seenAt) {
    const seenMs = Date.parse(seenAt);
    if (!Number.isNaN(seenMs) && Date.now() - seenMs < TTL_MS) continue;
  }

  staticData.seen[key] = nowIso;
  fresh.push(item);
}

// --- Prune old keys ---
for (const [key, ts] of Object.entries(staticData.seen)) {
  const ms = Date.parse(ts);
  if (Number.isNaN(ms) || Date.now() - ms > TTL_MS) delete staticData.seen[key];
}

if (fresh.length === 0) {
  return [{
    json: {
      competitorContext: `DATE=${nowIso.slice(0, 10)} | ITEMS=0\nTASK=No new items in last 24h.`,
      sources: [],
      stats: { input: inputItems.length, normalized: normalized.length, fresh: 0 },
      competitorTag: "mixed",
      date: nowIso.slice(0, 10),
    }
  }];
}

// ===== Reduce items to control token size =====
// Sort YT newest first
const yt = fresh
  .filter(x => x.sourceType === "youtube")
  .sort((a, b) => toMs(b.publishedAt) - toMs(a.publishedAt))
  .slice(0, MAX_YT_ITEMS);

// RSS: calculates the hot score -> gets the top 10 hottest topics.
const rssAll = fresh.filter(x => x.sourceType === "rss");
const rssHotTop10 = rssAll
  .map(x => ({ ...x, _hotScore: rssHotScore(x) }))
  .sort((a, b) => (b._hotScore || 0) - (a._hotScore || 0))
  .slice(0, RSS_HOT_POOL)
  .map(({ _hotScore, ...rest }) => rest);

// Final list: prioritize YouTube (max 2), then fill in the top 10 hottest RSS feeds until you have 8.
const final = [...yt];
for (const r of rssHotTop10) {
  if (final.length >= MAX_ITEMS_TOTAL) break;
  final.push(r);
}

// If there is still a shortage (low RSS), fill from the remaining (YT/RSS) according to recency.
if (final.length < MAX_ITEMS_TOTAL) {
  const finalIds = new Set(final.map(x => x.id));
  const remainder = fresh
    .filter(x => !finalIds.has(x.id))
    .sort((a, b) => toMs(b.publishedAt) - toMs(a.publishedAt));

  for (const it of remainder) {
    if (final.length >= MAX_ITEMS_TOTAL) break;
    final.push(it);
  }
}

// --- Build compact context (decrease token) ---
const lines = [];
lines.push(`DATE=${nowIso.slice(0, 10)} | ITEMS=${final.length}`);
lines.push(`TASK=insight>summary; detect positioning; propose 1 counter-tactic.`);
lines.push("");

final.forEach((it, idx) => {
  lines.push(`--#${idx + 1} ${it.sourceType.toUpperCase()} ${it.competitorTag}`);
  if (it.publishedAt) lines.push(`P=${it.publishedAt}`);
  lines.push(`T=${it.title}`);
  lines.push(`U=${it.url}`);
  lines.push(`C=${(it.content || "").replace(/\s+/g, " ").trim()}`);
  lines.push("");
});

const competitorTags = Array.from(new Set(final.map(x => x.competitorTag))).filter(Boolean);
const pageCompetitorTag = competitorTags.length === 1 ? competitorTags[0] : "mixed";

return [{
  json: {
    competitorContext: lines.join("\n"),
    sources: final,
    stats: { input: inputItems.length, normalized: normalized.length, fresh: final.length },
    competitorTag: pageCompetitorTag,
    date: nowIso.slice(0, 10),
    // debug optional:
    hotPool: {
      rssCandidates: rssAll.length,
      rssHotTop10: rssHotTop10.length,
      ytPicked: yt.length,
      final: final.length,
    }
  }
}];

function normalizeInline(s) {
  let t = String(s ?? "");

  // Convert basic HTML bold/italic to markdown
  t = t.replace(/<\/?b>/gi, "**");
  t = t.replace(/<\/?strong>/gi, "**");
  t = t.replace(/<\/?i>/gi, "*");
  t = t.replace(/<\/?em>/gi, "*");
  t = t.replace(/<br\s*\/?>/gi, "\n");

  // Strip any remaining html tags
  t = t.replace(/<[^>]+>/g, "");

  return t;
}

function pushText(arr, content, annotations = {}, link = null) {
  if (!content) return;
  arr.push({
    type: "text",
    text: {
      content,
      ...(link ? { link: { url: link } } : {}),
    },
    annotations: {
      bold: !!annotations.bold,
      italic: !!annotations.italic,
      strikethrough: !!annotations.strikethrough,
      underline: !!annotations.underline,
      code: !!annotations.code,
      color: "default",
    },
  });
}

// Inline markdown parser: **bold**, *italic*, `code`, URLs
function inlineToRichText(input) {
  const s0 = normalizeInline(input);
  const out = [];

  const tokenRegex =
    /(`[^`]+`)|(\*\*[^*]+\*\*)|(\*[^*]+\*)|(https?:\/\/[^\s)>"']+)/g;

  let last = 0;
  let m;
  while ((m = tokenRegex.exec(s0)) !== null) {
    if (m.index > last) pushText(out, s0.slice(last, m.index));

    const tok = m[0];

    if (tok.startsWith("`") && tok.endsWith("`")) {
      pushText(out, tok.slice(1, -1), { code: true });
    } else if (tok.startsWith("**") && tok.endsWith("**")) {
      pushText(out, tok.slice(2, -2), { bold: true });
    } else if (tok.startsWith("*") && tok.endsWith("*")) {
      pushText(out, tok.slice(1, -1), { italic: true });
    } else if (tok.startsWith("http")) {
      pushText(out, tok, {}, tok);
    } else {
      pushText(out, tok);
    }

    last = tokenRegex.lastIndex;
  }

  if (last < s0.length) pushText(out, s0.slice(last));

  return out.filter(x => x?.text?.content?.length);
}

function heading2(text) {
  return {
    object: "block",
    type: "heading_2",
    heading_2: { rich_text: inlineToRichText(text) },
  };
}

function divider() {
  return { object: "block", type: "divider", divider: {} };
}

function paragraph(text) {
  const t = String(text ?? "").trim();
  if (!t) return null;
  return {
    object: "block",
    type: "paragraph",
    paragraph: { rich_text: inlineToRichText(t) },
  };
}

function bullet(text) {
  const t = String(text ?? "").trim();
  if (!t) return null;
  return {
    object: "block",
    type: "bulleted_list_item",
    bulleted_list_item: { rich_text: inlineToRichText(t) },
  };
}

function toggle(title, children = []) {
  return {
    object: "block",
    type: "toggle",
    toggle: {
      rich_text: inlineToRichText(title),
      children,
    },
  };
}

function extractUrls(text) {
  const s = String(text ?? "");
  const urls = s.match(/https?:\/\/[^\s)>"']+/g) || [];
  const seen = new Set();
  const out = [];
  for (const u of urls) {
    const clean = u.replace(/[.,;]+$/g, "");
    if (!seen.has(clean)) {
      seen.add(clean);
      out.push(clean);
    }
  }
  return out;
}

function mdToBlocks(md) {
  const blocks = [];
  const lines = String(md ?? "").split(/\r?\n/);

  for (const raw of lines) {
    const line = raw.trim();
    if (!line) continue;

    if (line.startsWith("## ")) {
      blocks.push(heading2(line.replace(/^##\s+/, "")));
      continue;
    }

    if (line.startsWith("* ") || line.startsWith("- ")) {
      blocks.push(bullet(line.replace(/^(\*|\-)\s+/, "")));
      continue;
    }

    blocks.push(paragraph(line));
  }

  return blocks.filter(Boolean);
}

// ===== Input =====
const rp = $node["Code - Robust Parser (Gemini JSON)"].json;
const summary = rp.summary;
const strategyMd = rp.strategy_markdown;
const telegramHtml = rp.telegram_html;

// ===== Build page content (WITH ALL 3 EMOJI OPTIONS) =====
const children = [];

// Option #1: Emoji headings
children.push(heading2("üß† Summary"));
children.push(paragraph(summary || "(no summary)"));
children.push(divider());

// Strategy section
const strategyBlocks = mdToBlocks(strategyMd);
children.push(heading2("üß© Strategy"));

// Option #3: Emoji in toggle title
children.push(toggle("üëá Open strategy details", strategyBlocks));
children.push(divider());

// Sources
const urls = extractUrls(telegramHtml);
if (urls.length) {
  children.push(heading2("üîó Sources"));

  // Option #2: Emoji prefix for source bullets
  for (const u of urls.slice(0, 30)) children.push(bullet(`üîó ${u}`));
}

return [{ json: { children } }];

function extractJsonObject(text) {
  const t = (text ?? "").toString().trim();
  const s = t.indexOf("{");
  const e = t.lastIndexOf("}");
  if (s === -1 || e === -1 || e <= s) {
    throw new Error("No JSON object found in Gemini output");
  }
  return t.slice(s, e + 1);
}

function sanitizeTelegramHtml(html) {
  let s = (html ?? "").toString();

  // Telegram HTML DOES NOT support <br>, <ul>, <li>
  s = s.replace(/<br\s*\/?>/gi, "\n");
  s = s.replace(/<\/?ul[^>]*>/gi, "\n");
  s = s.replace(/<li[^>]*>\s*/gi, "‚Ä¢ ");
  s = s.replace(/<\/li>\s*/gi, "\n");

  // Convert links <a href="url">text</a> => "text: url"
  // (Telegram supports <a>, but Gemini often truncates mid-tag => easiest is to remove <a> entirely)
  s = s.replace(
    /<a\s+[^>]*href\s*=\s*"(.*?)"[^>]*>(.*?)<\/a>/gi,
    (_, url, text) => {
      const t = (text ?? "").replace(/<[^>]+>/g, "").trim();
      return t ? `${t}: ${url}` : url;
    }
  );

  // Remove ALL tags except Telegram-safe subset
  // Allowed: b/strong, i/em, u/ins, s/strike/del, code, pre
  s = s.replace(
    /<(?!\/?(b|strong|i|em|u|ins|s|strike|del|code|pre)\b)[^>]*>/gi,
    ""
  );

  // Normalize newlines
  s = s.replace(/\r\n/g, "\n");
  s = s.replace(/\n{3,}/g, "\n\n").trim();

  // If Gemini left unbalanced tags, safest is to strip ALL remaining tags
  // (Telegram will fail on unbalanced tags)
  const openB = (s.match(/<b>/gi) || []).length;
  const closeB = (s.match(/<\/b>/gi) || []).length;
  const openI = (s.match(/<i>/gi) || []).length;
  const closeI = (s.match(/<\/i>/gi) || []).length;
  const openU = (s.match(/<u>/gi) || []).length;
  const closeU = (s.match(/<\/u>/gi) || []).length;

  if (openB !== closeB || openI !== closeI || openU !== closeU) {
    s = s.replace(/<[^>]+>/g, "");
  }

  return s;
}

function safeTruncate(text, limit = 3800) {
  if (!text) return text;
  if (text.length <= limit) return text;

  // Cut near a newline to avoid ugly cut
  const cut = text.slice(0, limit - 30);
  const lastNl = cut.lastIndexOf("\n");
  const out = (lastNl > 200 ? cut.slice(0, lastNl) : cut).trim();

  return out + "\n\n(‚Ä¶truncated)";
}

// ---- Read Gemini node output (robust paths) ----
const j = $json;

const candidatesText =
  j?.candidates?.[0]?.content?.parts?.map(p => p.text).filter(Boolean).join("\n");

const possible =
  candidatesText ||
  j?.text ||
  j?.output ||
  j?.response ||
  j?.data ||
  j?.result ||
  j?.content ||
  j?.message ||
  (typeof j === "string" ? j : "");

const raw = extractJsonObject(possible);

let parsed;
try {
  parsed = JSON.parse(raw);
} catch (err) {
  throw new Error(`JSON.parse failed: ${err.message}\nRAW_JSON:\n${raw}`);
}

// ---- Sanitize + truncate for Telegram HTML parse mode ----
parsed.telegram_html = safeTruncate(sanitizeTelegramHtml(parsed.telegram_html), 4000);

// ---- Add meta (keep it the same) ----
return [{
  json: {
    ...parsed,
    meta: {
      generatedAt: new Date().toISOString(),
      competitorTag: $node["Code (Data Prep)"].json.competitorTag,
      date: $node["Code (Data Prep)"].json.date,
    }
  }
}];
