const xml = items[0].json.data;

// helper ambil isi tag
function getTag(text, tag) {
  const re = new RegExp('<' + tag + '>([\\s\\S]*?)<\\/' + tag + '>', 'i');
  const m = text.match(re);
  return m ? m[1].trim() : null;
}

// ambil semua <item>...</item>
const itemRegex = /<item>([\s\S]*?)<\/item>/g;
const itemBlocks = [];
let itemMatch;

while ((itemMatch = itemRegex.exec(xml)) !== null) {
  itemBlocks.push(itemMatch[1]);
}

const trends = itemBlocks.map(block => {
  // ambil news item
  const newsRegex = /<ht:news_item>([\s\S]*?)<\/ht:news_item>/g;
  const newsBlocks = [];
  let newsMatch;

  while ((newsMatch = newsRegex.exec(block)) !== null) {
    newsBlocks.push(newsMatch[1]);
  }

  return {
    keyword: getTag(block, 'title'),
    approx_traffic: getTag(block, 'ht:approx_traffic'),
    published_at: (() => {
      const d = getTag(block, 'pubDate');
      return d ? new Date(d).toISOString() : null;
    })(),
    image: {
      url: getTag(block, 'ht:picture'),
      source: getTag(block, 'ht:picture_source')
    },
    news: newsBlocks.map(n => ({
      title: getTag(n, 'ht:news_item_title'),
      url: getTag(n, 'ht:news_item_url'),
      source: getTag(n, 'ht:news_item_source'),
      image: getTag(n, 'ht:news_item_picture')
    }))
  };
});

return [
  {
    json: {
      source: 'Google Trends',
      geo: $('Edit Fields').first().json.region,
      generated_at: new Date().toISOString(),
      trends
    }
  }
];

const items = $input.all();
const keyword = items[0]?.json.searchQuery || "Trending";

return [{
    json: {
        platform: "tiktok",
        keyword: keyword,
        summary: items.map(i => ({
            caption: i.json.text || i.json.caption,
            views: i.json.playCount || 0,
            likes: i.json.diggCount || 0
        })).slice(0, 5) // Ambil 5 teratas
    }
}];

const items = $input.all();
const keyword = items[0]?.json.query || "Trending";

return [{
    json: {
        platform: "instagram",
        keyword: keyword,
        summary: items.map(i => ({
            caption: i.json.caption,
            likes: i.json.likesCount || i.json.edge_liked_by?.count || 0
        })).slice(0, 5)
    }
}];

const items = $input.all();
const keyword = items[0]?.json.keyword || "Trending";

return [{
    json: {
        platform: "x_twitter",
        keyword: keyword,
        summary: items.map(i => ({
            text: i.json.full_text || i.json.text,
            views: i.json.view_count || 0,
            likes: i.json.favorite_count || 0
        })).slice(0, 5)
    }
}];

// Ambil semua item hasil merge
const items = $items();

// Kembalikan sebagai STRING JSON
return [
  {
    json: {
      merged_data_text: JSON.stringify(items.map(i => i.json), null, 2)
    }
  }
];

const results = [];

for (const item of $items()) {
  let text = item.json.output;

  // hapus ```json ``` fence
  text = text.replace(/```json|```/g, '').trim();

  let parsed;
  try {
    parsed = JSON.parse(text);
  } catch (e) {
    parsed = {
      error: true,
      raw_output: text
    };
  }

  results.push({ json: parsed });
}

return results;
