// CONFIG NODE - All configuration in one place
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const CONFIG = {
  // â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”
  // â”‚ SKOOL CONFIGURATION                                         â”‚
  // â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜

  // Your Skool community slug (from URL: skool.com/YOUR-COMMUNITY)
  COMMUNITY: "your-community-slug",

  // Cookie - UPDATE THIS when it expires (check browser DevTools)
  // To get cookie: DevTools â†’ Application â†’ Cookies â†’ www.skool.com â†’ Copy all
  COOKIE: "YOUR_SKOOL_COOKIE_HERE",

  // Fallback BuildId (auto-discovered, kept as backup)
  FALLBACK_BUILD_ID: "1765216265055",

  // â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”
  // â”‚ CLAUDE AI CONFIGURATION                                     â”‚
  // â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜

  // Option 1: Use environment variable (recommended for security)
  // ANTHROPIC_API_KEY: $env.ANTHROPIC_API_KEY,
  // Option 2: Paste your key directly (not recommended for sharing)
  ANTHROPIC_API_KEY: "YOUR_ANTHROPIC_API_KEY",

  // Use Haiku for keyword extraction (cheaper & faster)
  KEYWORD_MODEL: "claude-3-haiku-20240307",
  KEYWORD_MAX_TOKENS: 50,

  // Use Sonnet for deep analysis
  ANALYSIS_MODEL: "claude-sonnet-4-20250514",
  ANALYSIS_MAX_TOKENS: 2500,

  // â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”
  // â”‚ GOOGLE DRIVE CONFIGURATION                                  â”‚
  // â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜

  // Your Google Drive folder ID for saving reports
  DEFAULT_FOLDER_ID: "YOUR_GOOGLE_DRIVE_FOLDER_ID",

  // â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”
  // â”‚ SEARCH & PROCESSING LIMITS                                  â”‚
  // â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜
  DEFAULT_SEARCH_DEPTH: 5,
  MAX_SEARCH_DEPTH: 10,
  MIN_SEARCH_DEPTH: 1,

  // Token protection limits
  MAX_POST_CONTENT_LENGTH: 2000,
  MAX_COMMENT_LENGTH: 500,
  MAX_COMMENTS_PER_POST: 5,
  MAX_POSTS_FOR_ANALYSIS: 20,

  // Filter old posts (in days) - set to 0 to disable
  MAX_POST_AGE_DAYS: 730  // 2 years
};

// Parse form inputs
let question = $json.question || $json.body?.question || "How to price Upwork jobs";
let folderId = $json.folder_id || $json.body?.folder_id || "";
let searchDepth = parseInt($json.search_depth || $json.body?.search_depth || CONFIG.DEFAULT_SEARCH_DEPTH);

// Validate search depth
if (isNaN(searchDepth) || searchDepth < CONFIG.MIN_SEARCH_DEPTH) {
  searchDepth = CONFIG.MIN_SEARCH_DEPTH;
} else if (searchDepth > CONFIG.MAX_SEARCH_DEPTH) {
  searchDepth = CONFIG.MAX_SEARCH_DEPTH;
}

// Use default folder if not provided
if (!folderId || folderId.trim() === "") {
  folderId = CONFIG.DEFAULT_FOLDER_ID;
}

return {
  json: {
    config: CONFIG,
    question: question,
    folderId: folderId,
    searchDepth: searchDepth,
    timestamp: new Date().toISOString()
  }
};

