// EXTRACT BUILD ID & VALIDATE COOKIE
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const html = $json.data || "";
const config = $('Config').first().json.config;
const question = $('Config').first().json.question;
const folderId = $('Config').first().json.folderId;
const searchDepth = $('Config').first().json.searchDepth;
const timestamp = $('Config').first().json.timestamp;

// â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”
// â”‚ CHECK FOR COOKIE EXPIRATION                                  â”‚
// â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜

const cookieExpired = 
  html.includes('"isAuthenticated":false') ||
  html.includes('Sign in to continue') ||
  html.includes('Log in to Skool') ||
  (html.length < 5000 && html.includes('login'));

if (cookieExpired) {
  return {
    json: {
      error: true,
      errorType: "COOKIE_EXPIRED",
      errorMessage: "Your Skool session has expired. Please update the cookie in the Config node.",
      question: question,
      folderId: folderId,
      config: config
    }
  };
}

// â”Œâ”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”
// â”‚ EXTRACT BUILD ID FROM HTML                                   â”‚
// â””â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”˜

let buildId = config.FALLBACK_BUILD_ID;
let buildIdSource = "fallback";

const buildIdMatch1 = html.match(/"buildId"\s*:\s*"([^"]+)"/);
if (buildIdMatch1 && buildIdMatch1[1]) {
  buildId = buildIdMatch1[1];
  buildIdSource = "extracted-json";
}

if (buildIdSource === "fallback") {
  const buildIdMatch2 = html.match(/_next\/data\/([^/]+)\//);
  if (buildIdMatch2 && buildIdMatch2[1]) {
    buildId = buildIdMatch2[1];
    buildIdSource = "extracted-script";
  }
}

if (buildIdSource === "fallback") {
  const buildIdMatch3 = html.match(/_next\/static\/([^/]+)\/_buildManifest/);
  if (buildIdMatch3 && buildIdMatch3[1]) {
    buildId = buildIdMatch3[1];
    buildIdSource = "extracted-manifest";
  }
}

return {
  json: {
    error: false,
    buildId: buildId,
    buildIdSource: buildIdSource,
    question: question,
    folderId: folderId,
    searchDepth: searchDepth,
    timestamp: timestamp,
    config: config
  }
};

