// BUILD KEYWORD EXTRACTION REQUEST (Using Claude Haiku - Cheaper)
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const data = $json;
const config = data.config;

const requestBody = {
  model: config.KEYWORD_MODEL,
  max_tokens: config.KEYWORD_MAX_TOKENS,
  messages: [
    {
      role: "user",
      content: "Extract 1-2 main search keywords only. Return ONLY keywords separated by spaces, nothing else. Question: " + data.question
    }
  ]
};

return {
  json: {
    requestBody: requestBody,
    apiKey: config.ANTHROPIC_API_KEY,
    ...data
  }
};

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// BUILD PAGE URLs - Dynamic BuildId, Configurable Depth
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const items = $input.all();
const keywordResponse = items[0].json;

const keywords = keywordResponse.content?.[0]?.text || "automation agency";

const prevData = $('Build Keyword Request').first().json;
const config = prevData.config;
const buildId = prevData.buildId;
const question = prevData.question;
const folderId = prevData.folderId;
const searchDepth = prevData.searchDepth;
const timestamp = prevData.timestamp;

const baseUrl = `https://www.skool.com/_next/data/${buildId}/${config.COMMUNITY}/-/search.json`;

const results = [];
for (let page = 1; page <= searchDepth; page++) {
  results.push({
    json: {
      url: `${baseUrl}?q=${encodeURIComponent(keywords)}&group=${config.COMMUNITY}&page=${page}`,
      page: page,
      keywords: keywords,
      cookie: config.COOKIE,
      question: question,
      folderId: folderId,
      searchDepth: searchDepth,
      buildId: buildId,
      config: config,
      timestamp: timestamp
    }
  });
}

return results;

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// AGGREGATE ALL POSTS - With Token Protection & Smart Filtering
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

const allItems = $input.all();
let allPosts = [];
let totalResults = 0;
let validPages = 0;
let errorPages = 0;

const firstItem = $('Build Page URLs').first().json;
const config = firstItem.config;
const question = firstItem.question;
const folderId = firstItem.folderId;
const searchDepth = firstItem.searchDepth;
const buildId = firstItem.buildId;
const timestamp = firstItem.timestamp;
const keywords = firstItem.keywords;

for (const item of allItems) {
  const json = item.json || {};

  if (json.error || !json.pageProps) {
    errorPages++;
    continue;
  }

  validPages++;
  const pageProps = json.pageProps;
  const postTrees = pageProps.postTrees || [];

  if (pageProps.totalPosts && pageProps.totalPosts > totalResults) {
    totalResults = pageProps.totalPosts;
  }

  for (const postItem of postTrees) {
    const post = postItem.post || {};
    const metadata = post.metadata || {};
    const user = post.user || {};
    const children = postItem.children || [];

    const firstName = user.firstName || "";
    const lastName = user.lastName || "";
    const authorName = (firstName + " " + lastName).trim() || "Anonymous";

    const comments = children
      .slice(0, config.MAX_COMMENTS_PER_POST)
      .map(function(child) {
        const c = child.post || {};
        const cu = c.user || {};
        const cMeta = c.metadata || {};
        const content = cMeta.content || "";

        return {
          author: ((cu.firstName || "") + " " + (cu.lastName || "")).trim() || "Anonymous",
          content: content.length > config.MAX_COMMENT_LENGTH 
            ? content.substring(0, config.MAX_COMMENT_LENGTH) + "..."
            : content,
          upvotes: cMeta.upvotes || 0,
          createdAt: c.createdAt || ""
        };
      });

    const rawContent = metadata.content || "";
    const truncatedContent = rawContent.length > config.MAX_POST_CONTENT_LENGTH
      ? rawContent.substring(0, config.MAX_POST_CONTENT_LENGTH) + "..."
      : rawContent;

    let includePost = true;
    if (config.MAX_POST_AGE_DAYS > 0 && post.createdAt) {
      const postDate = new Date(post.createdAt);
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - config.MAX_POST_AGE_DAYS);
      includePost = postDate >= cutoffDate;
    }

    if (includePost) {
      allPosts.push({
        id: post.id || "",
        title: metadata.title || "Untitled",
        content: truncatedContent,
        fullContent: rawContent,
        author: authorName,
        upvotes: metadata.upvotes || 0,
        commentCount: metadata.comments || 0,
        createdAt: post.createdAt || "",
        url: `https://www.skool.com/${config.COMMUNITY}/${post.name || ""}`,
        comments: comments
      });
    }
  }
}

const seenIds = {};
const uniquePosts = [];
for (const post of allPosts) {
  if (post.id && !seenIds[post.id]) {
    seenIds[post.id] = true;
    uniquePosts.push(post);
  }
}

uniquePosts.sort((a, b) => (b.upvotes + b.commentCount) - (a.upvotes + a.commentCount));

return [{
  json: {
    question: question,
    totalResults: totalResults,
    pagesSearched: validPages,
    errorPages: errorPages,
    posts: uniquePosts,
    folderId: folderId,
    buildId: buildId,
    keywords: keywords,
    config: config,
    timestamp: timestamp
  }
}];

