// Helper function to extract core company name from domain
function extractCompanyName(domain) {
  return domain
    .replace(/^https?:\/\//, '')  // Remove protocol
    .replace(/^www\./, '')         // Remove www
    .replace(/\/$/, '')            // Remove trailing slash
    .replace(/\.(com|net|org|io|co|ai|us|uk|biz|info|be)\/.*$/, '')  // Remove extension + path
    .replace(/\.(com|net|org|io|co|ai|us|uk|biz|info|be)$/, '')      // Remove extension
    .toLowerCase();
}

// Get the loop domain and extract company name
const loopDomain = $('Loop Over Leads').first().json['Website Domain'];
const loopCompanyName = extractCompanyName(loopDomain);

// Get the list from the API response
const list = $input.first().json.data.list;

// TIER 1: Exact domain match (with any TLD)
const exactMatch = list.find(item => {
  const itemCompanyName = extractCompanyName(item.website);
  return itemCompanyName === loopCompanyName;
});

if (exactMatch) {
  return exactMatch;
}

// TIER 2: Check if company name appears in the 'name' field
const nameMatch = list.find(item => {
  if (!item.name) return false;
  const itemNameClean = item.name.toLowerCase().replace(/[^a-z0-9]/g, '');
  const loopNameClean = loopCompanyName.replace(/[^a-z0-9]/g, '');
  return itemNameClean === loopNameClean || 
         itemNameClean.includes(loopNameClean) ||
         loopNameClean.includes(itemNameClean);
});

if (nameMatch) {
  return nameMatch;
}

// TIER 3: Check LinkedIn URL (often most reliable)
const linkedinMatch = list.find(item => {
  if (!item.linkedinUrl) return false;
  const linkedinName = item.linkedinUrl
    .replace('linkedin.com/company/', '')
    .replace(/[^a-z0-9]/g, '')
    .toLowerCase();
  const loopNameClean = loopCompanyName.replace(/[^a-z0-9]/g, '');

  return linkedinName === loopNameClean;
});

if (linkedinMatch) {
  return linkedinMatch;
}

// TIER 4: Partial match with strict rules (must start with same name)
const partialMatch = list.find(item => {
  const itemCompanyName = extractCompanyName(item.website);

  // Only match if one STARTS with the other (not just contains)
  return itemCompanyName.startsWith(loopCompanyName) || 
         loopCompanyName.startsWith(itemCompanyName);
});

if (partialMatch) {
  return partialMatch;
}

// TIER 5: No match found, return first item as fallback
return list[0];

