const emailData = $input.first().json.output;
const researchData = $('Company Research AI').first().json.output;
const leadData = $('Loop Over Leads').first().json;

// Create Gmail compose link
const subject = encodeURIComponent(emailData.subject_line);
const body = encodeURIComponent(emailData.email_body);
const gmailLink = `https://mail.google.com/mail/?view=cm&fs=1&to=${leadData.Email}&su=${subject}&body=${body}`;

return {
  'Website Domain': leadData['Website Domain'],
  'Company Name': leadData['Company Name'],
  'Contact Name': leadData['Contact Name'],
  'Email': leadData.Email,
  'Industry': leadData.Industry,
  'Research Status': 'Completed',
  'Pain Points': researchData.pain_points.join('; '),
  'Key Insight': researchData.one_liner_insight,
  'Email Subject': emailData.subject_line,
  'Email Body': emailData.email_body,
  'Send Email Link': gmailLink,
  'Generated Date': new Date().toISOString().split('T')[0],
  'Sent Status': 'Not Sent'
};
