// Parse Telegram message
const messageText = $input.item.json.message.text;
const lines = messageText.split('\n');

// Extract first line as yt_url and second line as user_desc
const yt_url = lines[0] || '';
const user_desc = lines[1] || '';

// Return the parsed data
return {
  json: {
    yt_url: yt_url.trim(),
    user_desc: user_desc.trim()
  }
};

// Extract video ID from YouTube URL
const url = $input.item.json.Url;

let videoId = null;

if (url) {
  // youtu.be format
  const youtuBeMatch = url.match(/youtu\.be\/([^?&]+)/);
  if (youtuBeMatch) {
    videoId = youtuBeMatch[1];
  } else {
    // youtube.com/watch?v=
    const youtubeMatch = url.match(/[?&]v=([^?&]+)/);
    if (youtubeMatch) {
      videoId = youtubeMatch[1];
    }
  }
}

return {
  json: {
    videoId
  }
};

// ‚úÖ n8n Code node (Run Once for Each Item)
// Input attendu: $input.item.json.data.transcripts...

const data = $input.item.json.data;
if (!data) throw new Error("Champ 'data' introuvable dans l'input.");

const transcripts = data.transcripts;
if (!transcripts || typeof transcripts !== "object") {
  throw new Error("Champ 'data.transcripts' introuvable.");
}

// 1) D√©terminer la langue (ex: fr_auto) si fournie
const preferredLang =
  (Array.isArray(data.language_code) && data.language_code[0]?.code) || null;

// 2) Chercher le meilleur objet de transcription
const langObj =
  (preferredLang && transcripts[preferredLang]) ||
  transcripts.fr_auto ||
  transcripts.en_auto ||
  transcripts[Object.keys(transcripts)[0]];

if (!langObj) throw new Error("Aucune langue de transcription trouv√©e.");

// 3) Choisir le meilleur tableau (priorit√©: custom > auto > default)
const segs =
  langObj.custom ||
  langObj.auto ||
  langObj.default;

if (!Array.isArray(segs)) {
  throw new Error("Aucun tableau de segments trouv√© (custom/auto/default).");
}

// 4) Joindre tous les segments texte
let text = segs
  .map(s => (s?.text ?? "").toString().trim())
  .filter(Boolean)
  .join(" ");

// 5) Nettoyage l√©ger + d√©codage des entit√©s HTML fr√©quentes
text = text
  .replace(/&amp;#39;|&#39;/g, "'")
  .replace(/&quot;|&#34;/g, '"')
  .replace(/&amp;/g, "&")
  .replace(/\s+/g, " ")
  .trim();

return {
  json: {
    videoId: data.videoId,
    language: preferredLang || null,
    clean_transcript: text
  }
};

