// Parse and structure IoT sensor data
const items = $input.all();
const structuredData = [];

for (const item of items) {
  const rawData = item.json;

  // Structure the IoT sensor data
  const structured = {
    timestamp: rawData.timestamp || new Date().toISOString(),
    energyUsage: rawData.energyUsage || rawData.energy || 0,
    waterUsage: rawData.waterUsage || rawData.water || 0,
    wasteGenerated: rawData.wasteGenerated || rawData.waste || 0,
    location: rawData.location || 'Unknown',
    sensorId: rawData.sensorId || rawData.id || 'Unknown'
  };

  structuredData.push({ json: structured });
}

return structuredData;

