// Get values from the input item
const currentPriceStr = $input.first().json.current_price;
const originalPriceStr = $input.first().json.original_price;

// Detect currency symbol (assumes same for both)
const currencySymbol = currentPriceStr.trim().charAt(0);

// Clean currency symbols and commas, then parse to float
const currentPrice = parseFloat(currentPriceStr.replace(/[^0-9.]/g, '').replace(/,/g, ''));
const originalPrice = parseFloat(originalPriceStr.replace(/[^0-9.]/g, '').replace(/,/g, ''));

// Calculate discount
const discount = ((originalPrice - currentPrice) / originalPrice) * 100;
const discountPercentage = discount.toFixed(2);

// Return output with original currency symbol
return [
  {
    json: {
      current_price: `${currencySymbol}${currentPrice}`,
      original_price: `${currencySymbol}${originalPrice}`,
      discount_percentage: `${discountPercentage}`
    }
  }
];

