parameters.jsCode:
const action = $input.item.json.body.actions[0].action_id;
const prev = $('üîß Prepare Data').item.json;

let result = {
  blog_approved: prev.blog_approved,
  linkedin_approved: prev.linkedin_approved,
  twitter_approved: prev.twitter_approved,
  instagram_approved: prev.instagram_approved,
  action: action.startsWith('approve_') ? 'approve_platform' : action,
  content_data: $('üîç Parse JSON').item.json.content_data,
  transcript_text: prev.transcript_text,
  video_url: prev.video_url,
  revision_count: prev.revision_count
};

if (action === 'approve_blog') result.blog_approved = true;
if (action === 'approve_linkedin') result.linkedin_approved = true;
if (action === 'approve_twitter') result.twitter_approved = true;
if (action === 'approve_instagram') result.instagram_approved = true;

return result;

