// Extract the incoming webhook data
const incoming = $json;

// Extract only the relevant fields from the body
const body = incoming.body || {};

// Build the cleaned, structured output
const cleaned = {
  action: body.action || "",
  organizationName: body.organizationName || "",
  applicationType: body.applicationType || "",
  contactEmail: body.contactEmail || "",
  netWorth: body.netWorth || "",
  technicalCapacity: body.technicalCapacity || "",
  operationalCapacity: body.operationalCapacity || "",
  documentAttached: body.documentAttached || null,
  submittedAt: body.submittedAt || ""
};

// Return result
return [
  {
    json: cleaned
  }
];

const raw = $json.output;

// Remove markdown ```json ``` fences
const cleaned = raw.replace(/```json|```/g, '').trim();

// Convert to actual JSON
const parsed = JSON.parse(cleaned);

return parsed;

