parameters.jsCode:
// Get the minimum investment score threshold from Workflow Configuration
const minInvestmentScore = $('Workflow Configuration').item.json.minInvestmentScore || 70;

// Get the AI agent output
const agentOutput = $('Investment Opportunity Analyzer').item.json;

// Extract opportunities array from the agent output
let opportunities = [];
if (agentOutput.opportunities && Array.isArray(agentOutput.opportunities)) {
  opportunities = agentOutput.opportunities;
} else if (agentOutput.output && agentOutput.output.opportunities && Array.isArray(agentOutput.output.opportunities)) {
  opportunities = agentOutput.output.opportunities;
} else if (Array.isArray(agentOutput)) {
  opportunities = agentOutput;
}

// Filter opportunities where investmentScore >= minInvestmentScore
const filteredOpportunities = opportunities.filter(opp => {
  const score = opp.investmentScore || opp.investment_score || 0;
  return score >= minInvestmentScore;
});

// Sort by investmentScore descending
filteredOpportunities.sort((a, b) => {
  const scoreA = a.investmentScore || a.investment_score || 0;
  const scoreB = b.investmentScore || b.investment_score || 0;
  return scoreB - scoreA;
});

// Return filtered and sorted opportunities
return filteredOpportunities.map(opp => ({ json: opp }));

