// Analyze claims data and detect cost leakage anomalies

// Configuration thresholds
const EXCESSIVE_PAYOUT_THRESHOLD = 50000; // Flag payouts above this amount
const RESERVE_VARIANCE_THRESHOLD = 0.25; // 25% variance threshold

// Get all input items (claims data)
const claims = $input.all();

// Array to store detected anomalies
const anomalies = [];

// Process each claim
for (const claim of claims) {
  const claimData = claim.json;
  const claimId = claimData.claimId || claimData.id || 'UNKNOWN';

  // 1. Check for excessive payouts
  if (claimData.payoutAmount && claimData.payoutAmount > EXCESSIVE_PAYOUT_THRESHOLD) {
    anomalies.push({
      claimId: claimId,
      leakageType: 'Excessive Payout',
      amount: claimData.payoutAmount,
      variancePercentage: ((claimData.payoutAmount - EXCESSIVE_PAYOUT_THRESHOLD) / EXCESSIVE_PAYOUT_THRESHOLD * 100).toFixed(2),
      details: `Payout of $${claimData.payoutAmount} exceeds threshold of $${EXCESSIVE_PAYOUT_THRESHOLD}`,
      severity: 'HIGH'
    });
  }

  // 2. Check for inconsistent reserves (variance > 25%)
  if (claimData.reserveAmount && claimData.actualAmount) {
    const variance = Math.abs(claimData.actualAmount - claimData.reserveAmount) / claimData.reserveAmount;
    if (variance > RESERVE_VARIANCE_THRESHOLD) {
      anomalies.push({
        claimId: claimId,
        leakageType: 'Inconsistent Reserve',
        amount: Math.abs(claimData.actualAmount - claimData.reserveAmount),
        variancePercentage: (variance * 100).toFixed(2),
        details: `Reserve: $${claimData.reserveAmount}, Actual: $${claimData.actualAmount}, Variance: ${(variance * 100).toFixed(2)}%`,
        severity: variance > 0.5 ? 'HIGH' : 'MEDIUM'
      });
    }
  }

  // 3. Check for improper vendor charges (duplicates/inflated)
  if (claimData.vendorCharges && Array.isArray(claimData.vendorCharges)) {
    const chargeMap = {};

    for (const charge of claimData.vendorCharges) {
      const key = `${charge.vendorId}_${charge.serviceType}_${charge.date}`;

      // Check for duplicates
      if (chargeMap[key]) {
        anomalies.push({
          claimId: claimId,
          leakageType: 'Duplicate Vendor Charge',
          amount: charge.amount,
          variancePercentage: 100,
          details: `Duplicate charge from vendor ${charge.vendorId} for ${charge.serviceType} on ${charge.date}`,
          severity: 'HIGH'
        });
      } else {
        chargeMap[key] = charge;
      }

      // Check for inflated charges (if expected amount is provided)
      if (charge.expectedAmount && charge.amount > charge.expectedAmount * 1.25) {
        const inflationVariance = ((charge.amount - charge.expectedAmount) / charge.expectedAmount * 100).toFixed(2);
        anomalies.push({
          claimId: claimId,
          leakageType: 'Inflated Vendor Charge',
          amount: charge.amount - charge.expectedAmount,
          variancePercentage: inflationVariance,
          details: `Vendor ${charge.vendorId} charged $${charge.amount} vs expected $${charge.expectedAmount}`,
          severity: 'MEDIUM'
        });
      }
    }
  }
}

// Return results
if (anomalies.length > 0) {
  return {
    json: {
      leakageDetected: true,
      totalAnomalies: anomalies.length,
      totalLeakageAmount: anomalies.reduce((sum, a) => sum + (a.amount || 0), 0),
      anomalies: anomalies,
      analysisDate: new Date().toISOString(),
      claimsAnalyzed: claims.length
    }
  };
} else {
  return {
    json: {
      leakageDetected: false,
      totalAnomalies: 0,
      totalLeakageAmount: 0,
      anomalies: [],
      analysisDate: new Date().toISOString(),
      claimsAnalyzed: claims.length
    }
  };
}

