// Generate corrective billing adjustments based on AI classification
const item = $input.item.json;

// Extract AI classification results
const rootCause = item.root_cause || item.rootCause || 'UNKNOWN';
const leakageAmount = item.leakage_amount || item.leakageAmount || 0;
const claimId = item.claim_id || item.claimId || 'UNKNOWN';
const confidence = item.confidence || 0.5;

// Determine adjustment type based on root cause
let adjustmentType;
let recommendedAction;
let priorityLevel;

switch(rootCause.toUpperCase()) {
  case 'DUPLICATE_PAYMENT':
  case 'OVERPAYMENT':
    adjustmentType = 'RECOVER_OVERPAYMENT';
    recommendedAction = 'Initiate recovery process for duplicate/overpayment';
    priorityLevel = 'HIGH';
    break;

  case 'INCORRECT_PRICING':
  case 'BILLING_ERROR':
    adjustmentType = 'REDUCE_PAYMENT';
    recommendedAction = 'Adjust payment to correct pricing/billing amount';
    priorityLevel = 'MEDIUM';
    break;

  case 'RESERVE_MISCALCULATION':
  case 'RESERVE_ERROR':
    adjustmentType = 'CORRECT_RESERVE';
    recommendedAction = 'Recalculate and adjust reserve allocation';
    priorityLevel = 'MEDIUM';
    break;

  case 'VENDOR_OVERCHARGE':
  case 'PROVIDER_FRAUD':
    adjustmentType = 'VENDOR_DISPUTE';
    recommendedAction = 'Initiate vendor dispute and investigation';
    priorityLevel = 'HIGH';
    break;

  default:
    adjustmentType = 'REDUCE_PAYMENT';
    recommendedAction = 'Review and adjust payment as needed';
    priorityLevel = 'LOW';
}

// Calculate adjustment amount (negative for reductions/recoveries)
const adjustmentAmount = -Math.abs(leakageAmount);

// Generate justification
const justification = `Cost leakage detected via AI analysis. Root cause identified as ${rootCause} with ${(confidence * 100).toFixed(1)}% confidence. Recommended corrective action to recover $${Math.abs(adjustmentAmount).toFixed(2)}.`;

// Return corrective adjustment object
return {
  claim_id: claimId,
  adjustment_type: adjustmentType,
  recommended_action: recommendedAction,
  adjustment_amount: adjustmentAmount,
  original_leakage_amount: leakageAmount,
  justification: justification,
  priority_level: priorityLevel,
  root_cause: rootCause,
  confidence_score: confidence,
  generated_at: new Date().toISOString(),
  status: 'PENDING_REVIEW'
};

// Calculate risk score based on multiple weighted factors
const item = $input.item.json;

// Extract input data
const historicalFrequency = item.historicalFrequency || item.pattern_frequency || 0;
const leakageAmount = item.leakageAmount || item.amount || item.adjustment_amount || 0;
const vendorReputation = item.vendorReputation || item.vendor_reputation_score || 50; // Default to neutral
const policyViolationSeverity = item.policyViolationSeverity || item.policy_violation_severity || 0;

// Define weights
const WEIGHT_HISTORICAL = 0.3;
const WEIGHT_AMOUNT = 0.4;
const WEIGHT_VENDOR = 0.2;
const WEIGHT_POLICY = 0.1;

// Normalize historical frequency (assume 0-10 scale, normalize to 0-100)
const normalizedHistorical = Math.min((historicalFrequency / 10) * 100, 100);

// Normalize leakage amount (assume $0-$50000 scale, normalize to 0-100)
const normalizedAmount = Math.min((Math.abs(leakageAmount) / 50000) * 100, 100);

// Vendor reputation is already 0-100, but invert it (lower reputation = higher risk)
const normalizedVendor = 100 - vendorReputation;

// Policy violation severity (assume 0-10 scale, normalize to 0-100)
const normalizedPolicy = Math.min((policyViolationSeverity / 10) * 100, 100);

// Calculate weighted risk score
const riskScore = Math.round(
  (normalizedHistorical * WEIGHT_HISTORICAL) +
  (normalizedAmount * WEIGHT_AMOUNT) +
  (normalizedVendor * WEIGHT_VENDOR) +
  (normalizedPolicy * WEIGHT_POLICY)
);

// Determine risk category
let riskCategory;
if (riskScore >= 80) {
  riskCategory = 'CRITICAL';
} else if (riskScore >= 60) {
  riskCategory = 'HIGH';
} else if (riskScore >= 40) {
  riskCategory = 'MEDIUM';
} else {
  riskCategory = 'LOW';
}

// Return enriched data with risk score and category
return {
  ...item,
  riskScore: riskScore,
  riskCategory: riskCategory,
  riskFactors: {
    historicalFrequency: normalizedHistorical,
    leakageAmount: normalizedAmount,
    vendorReputation: normalizedVendor,
    policyViolation: normalizedPolicy
  },
  calculatedAt: new Date().toISOString()
};
