parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "isAuthentic": {
      "type": "boolean",
      "description": "Whether the document is authentic"
    },
    "confidenceScore": {
      "type": "number",
      "description": "Confidence score from 0-100"
    },
    "findings": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "List of specific findings"
    },
    "riskLevel": {
      "type": "string",
      "enum": ["low", "medium", "high", "critical"],
      "description": "Risk level assessment"
    },
    "recommendation": {
      "type": "string",
      "enum": ["approve", "review", "reject"],
      "description": "Recommended action"
    }
  },
  "required": ["isAuthentic", "confidenceScore", "findings", "riskLevel", "recommendation"]
}
