=## Input Data
You are given:

product_title : {{ $('input update').item.json.title }}
product_description (HTML from Shopify) : {{ $('input update').item.json.body_html }}
brand_name : {{ $('input update').item.json.vendor }}
product_type : {{ $('input update').item.json.product_type }}
websearch_data (research summary about this product/type) : {{ $json.message }}

parameters.jsCode:
// Extract data from AI Agent AND Shopify product node
const aiData = $input.first().json.output;
const productData = $('input update').first().json;

// Get the first product image URL (Shopify format)
const productImage = productData.images && productData.images.length > 0 
  ? productData.images[0].src 
  : 'https://via.placeholder.com/800x400/2563eb/ffffff?text=No+Image+Available';

// Clean up any problematic newlines from AI output
function cleanText(text) {
  if (!text) return '';
  return text
    .replace(/\n\s*\n/g, ' ')
    .replace(/[\r\n]+/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();
}

// Shopify-safe JSON escaping function
function makeJsonSafe(text) {
  if (!text) return '';
  return text
    .replace(/\\/g, '\\\\')     // Escape backslashes first
    .replace(/"/g, '\\"')       // Escape double quotes
    .replace(/\n/g, '\\n')      // Escape newlines
    .replace(/\r/g, '');        // Remove carriage returns
}

// Clean and escape ALL text fields
const cleanedData = {
  article_title: makeJsonSafe(cleanText(aiData.article_title)),
  introduction: makeJsonSafe(cleanText(aiData.introduction)),
  problem_title: makeJsonSafe(cleanText(aiData.problem_title)),
  problem_paragraph: makeJsonSafe(cleanText(aiData.problem_paragraph)),
  solution_title: makeJsonSafe(cleanText(aiData.solution_title)),
  solution_paragraph: makeJsonSafe(cleanText(aiData.solution_paragraph)),
  problem_pointers: aiData.problem_pointers.map(p => ({
    name: makeJsonSafe(cleanText(p.name)),
    value: makeJsonSafe(cleanText(p.value))
  })),
  features_and_benefits: aiData.features_and_benefits.map(fb => ({
    name: makeJsonSafe(cleanText(fb.name)),
    value: makeJsonSafe(cleanText(fb.value))
  })),
  technical_specifications: aiData.technical_specifications.map(spec => ({
    name: makeJsonSafe(cleanText(spec.name)),
    value: makeJsonSafe(cleanText(spec.value))
  })),
  real_life_usage: aiData.real_life_usage.map(scenario => ({
    title: makeJsonSafe(cleanText(scenario.title)),
    description: makeJsonSafe(cleanText(scenario.description)),
    points: scenario.points.map(point => ({
      name: makeJsonSafe(cleanText(point.name)),
      value: makeJsonSafe(cleanText(point.value))
    }))
  })),
  comparative_advantage: aiData.comparative_advantage.map(comp => ({
    factor: makeJsonSafe(cleanText(comp.factor)),
    this_product: makeJsonSafe(cleanText(comp.this_product)),
    others: makeJsonSafe(cleanText(comp.others))
  })),
  recommendations_and_cta: {
    title: makeJsonSafe(cleanText(aiData.recommendations_and_cta.title)),
    description: makeJsonSafe(cleanText(aiData.recommendations_and_cta.description))
  }
};

// Build BODY HTML (no escaping needed here - HTML content)
const bodyHtmlParts = [
  `<div class="hero" style="background:linear-gradient(135deg,#667eea 0%,#764ba2 100%);color:white;padding:2.5rem;border-radius:20px;margin:2rem 0;box-shadow:0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);text-align:center;position:relative;overflow:hidden;"><h1 style="margin:0;font-size:2.5rem;font-weight:800;">${cleanedData.article_title}</h1><p style="font-size:1.2rem;margin:1rem 0 0;opacity:0.95;">${cleanedData.introduction}</p></div>`,

  `<div class="product-image" style="text-align:center;margin:3rem 0;"><img src="${productImage}" alt="${cleanedData.article_title}" loading="lazy" style="max-width:100%;max-height:400px;height:auto;border-radius:24px;box-shadow:0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);border:4px solid white;"></div>`,

  `<div class="card" style="background:white;border-radius:16px;padding:2rem;margin:1.5rem 0;box-shadow:0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);border:1px solid #e2e8f0;"><h2 style="font-size:1.75rem;font-weight:700;color:#1f2937;margin:0 0 1.5rem;padding-bottom:0.75rem;border-bottom:3px solid #2563eb;">${cleanedData.problem_title}</h2><p style="margin-bottom:1.5rem;line-height:1.7;">${cleanedData.problem_paragraph}</p><ul style="margin-top:1rem;list-style:none;padding:0;">${cleanedData.problem_pointers.map(pointer => `<li style="padding:1rem;margin:0.75rem 0;background:linear-gradient(90deg,#f8fafc,#f1f5f9);border-radius:12px;border-left:4px solid #2563eb;font-size:1rem;"><strong style="color:#1e40af;display:block;margin-bottom:0.25rem;font-size:1.05rem;">${pointer.name}</strong> ${pointer.value}</li>`).join('')}</ul></div>`,

  `<div class="card" style="background:white;border-radius:16px;padding:2rem;margin:1.5rem 0;box-shadow:0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);border:1px solid #e2e8f0;"><h2 style="font-size:1.75rem;font-weight:700;color:#1f2937;margin:0 0 1.5rem;padding-bottom:0.75rem;border-bottom:3px solid #2563eb;">${cleanedData.solution_title}</h2><p style="line-height:1.7;">${cleanedData.solution_paragraph}</p></div>`,

  `<div class="card" style="background:white;border-radius:16px;padding:2rem;margin:1.5rem 0;box-shadow:0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);border:1px solid #e2e8f0;"><h2 style="font-size:1.75rem;font-weight:700;color:#1f2937;margin:0 0 1.5rem;padding-bottom:0.75rem;border-bottom:3px solid #2563eb;">Key Features & Benefits</h2><ul style="margin-top:1rem;list-style:none;padding:0;">${cleanedData.features_and_benefits.map(fb => `<li style="padding:1rem;margin:0.75rem 0;background:linear-gradient(90deg,#f8fafc,#f1f5f9);border-radius:12px;border-left:4px solid #2563eb;font-size:1rem;"><strong style="color:#1e40af;display:block;margin-bottom:0.25rem;font-size:1.05rem;">${fb.name}</strong><br>${fb.value}</li>`).join('')}</ul></div>`,

  `<div class="card" style="background:white;border-radius:16px;padding:2rem;margin:1.5rem 0;box-shadow:0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);border:1px solid #e2e8f0;"><h2 style="font-size:1.75rem;font-weight:700;color:#1f2937;margin:0 0 1.5rem;padding-bottom:0.75rem;border-bottom:3px solid #2563eb;">Technical Specifications</h2><ul style="display:grid;grid-template-columns:repeat(auto-fit,minmax(250px,1fr));gap:1rem;margin:1.5rem 0 0;list-style:none;padding:0;">${cleanedData.technical_specifications.map(spec => `<li style="padding:1.25rem;border-radius:12px;background:#f0f9ff;border:1px solid #bae6fd;"><strong style="color:#1e40af;">${spec.name}:</strong> ${spec.value}</li>`).join('')}</ul></div>`,

  `<h2 style="font-size:1.75rem;font-weight:700;color:#1f2937;margin:3rem 0 1.5rem;padding-bottom:0.75rem;border-bottom:3px solid #2563eb;">Real Life Usage</h2>${cleanedData.real_life_usage.map(scenario => `<div class="usage-card" style="background:linear-gradient(135deg,#fef3c7 0%,#fde68a 100%);border:1px solid #f59e0b;border-radius:16px;overflow:hidden;margin:2rem 0;box-shadow:0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);"><h3 style="background:#f59e0b;color:white;padding:1rem 1.5rem;margin:0;font-size:1.2rem;font-weight:600;">${scenario.title}</h3><div class="usage-meta" style="padding:1rem 1.5rem 0.5rem;color:#92400e;font-style:italic;">${scenario.description}</div><ul style="margin:0;padding-left:1.5rem;">${scenario.points.map(point => `<li style="padding-left:1.5rem;margin:0.75rem 0;"><strong style="display:block;margin-bottom:0.25rem;font-size:1.05rem;color:#1e40af;">${point.name}:</strong> ${point.value}</li>`).join('')}</ul></div>`).join('')}`,

  `<div class="card" style="background:white;border-radius:16px;padding:2rem;margin:1.5rem 0;box-shadow:0 1px 3px 0 rgb(0 0 0 / 0.1), 0 1px 2px -1px rgb(0 0 0 / 0.1);border:1px solid #e2e8f0;"><h2 style="font-size:1.75rem;font-weight:700;color:#1f2937;margin:0 0 1.5rem;padding-bottom:0.75rem;border-bottom:3px solid #2563eb;">Comparative Advantage</h2><table style="width:100%;border-collapse:collapse;border-spacing:0;margin:2rem 0 0;"><tr style="background:linear-gradient(135deg,#2563eb,#1e40af);"><th style="color:white;padding:1.25rem 1rem;font-weight:600;text-align:left;">Factor</th><th style="color:white;padding:1.25rem 1rem;font-weight:600;text-align:left;">${cleanedData.article_title.split(':')[0]} Advantage</th><th style="color:white;padding:1.25rem 1rem;font-weight:600;text-align:left;">Competitors</th></tr>${cleanedData.comparative_advantage.map(comp => `<tr><td style="padding:1.25rem 1rem;border-bottom:1px solid #e2e8f0;font-weight:600;color:#1e40af;width:25%;">${comp.factor}</td><td style="padding:1.25rem 1rem;border-bottom:1px solid #e2e8f0;">${comp.this_product}</td><td style="padding:1.25rem 1rem;border-bottom:1px solid #e2e8f0;">${comp.others}</td></tr>`).join('')}</table></div>`,

  `<div class="cta" style="background:linear-gradient(135deg,#2563eb,#1e40af);color:white;padding:3rem 2rem;border-radius:24px;text-align:center;margin:4rem 0 2rem;box-shadow:0 10px 15px -3px rgb(0 0 0 / 0.1), 0 4px 6px -4px rgb(0 0 0 / 0.1);"><h2 style="font-size:2rem;margin:0 0 1rem;font-weight:700;">${cleanedData.recommendations_and_cta.title}</h2><p style="font-size:1.15rem;margin:0;line-height:1.6;">${cleanedData.recommendations_and_cta.description}</p></div>`
];

// Join and clean body_html
const rawBodyHtml = bodyHtmlParts.join('').replace(/[\r\n]+/g, '');
const body_html = makeJsonSafe(rawBodyHtml); // FINAL JSON escaping

// Return PERFECTLY clean Shopify JSON
return [{
  json: {
    article: {
      title: cleanedData.article_title,
      body_html: body_html,
      excerpt: cleanedData.introduction.substring(0, 160) + '...',
      published: true,
      tags: "product-review, featured"
    }
  }
}];
