// 1. Grab data from the previous nodes
const aggregatedItems = $items("Compile History Array");
const driveFiles = $items("Scan Portfolio Assets");

// 2. Extract the Topic names from the Aggregated list
// We reach into the first item [0], then the 'data' array
const publishedTopics = aggregatedItems[0].json.data.map(post => {
  return post.Topic?.trim();
}).filter(Boolean);

// 3. Filter the Drive files
const unpostedFiles = driveFiles.filter(file => {
  // Remove extension (e.g., .pdf)
  const fileNameClean = file.json.name.replace(/\.[^/.]+$/, "").trim();

  // Return true ONLY if the filename is NOT in our published list
  return !publishedTopics.includes(fileNameClean);
});

// 4. Return the result
return unpostedFiles;

let optionCount = 1;
const allOptions = [];

const items = $input.all();

for (let i = 0; i < items.length; i++) {
  let content = items[i].json.output || "";

  // 1. Sanitization
  content = content.replace(/^["']|["']$/g, ''); // Remove quotes
  content = content.replace(/[\u2014\u2015]/g, '-'); // Fix dashes

  // 2. Formatting the CTA
  if (content.includes("Swipe through")) {
    content = content.replace(/\s*(Swipe through.*)/i, '\n\n$1');
  }

  // 3. Robust Node Naming
  // This looks at the specific input item to find which node it came from
  let nodeName = items[i].pairedItem?.node?.name;

  // Fallback if metadata is missing: 
  // Usually, Item 0 is the top input (GPT) and Item 1 is the bottom (Claude)
  if (!nodeName) {
    nodeName = (i === 0) ? "Draft Portfolio Post (GPT-4o)" : "Draft Portfolio Post (Claude 4.5)";
  }

  allOptions.push({
    json: {
      agent: nodeName,
      option: optionCount++, 
      content: content.trim()
    }
  });
}

return allOptions;

