// 1. Get all items from the Merge node (GPT, Claude, Gemini)
const items = $input.all();

// 2. Get the specific topic data from your Google Sheet node
let topicTitle = "Agency Strategy Topic";
let rowId = "";

try {
  // Pulls from the 'Select Single Topic' node
  const sheetNode = $("Select Single Topic").first().json;
  topicTitle = sheetNode.Topic || topicTitle;
  rowId = sheetNode.row_number || "";
} catch (e) {
  // Fallback if the reference fails
}

// 3. Helper Function to Clean and Sanitize Text
const cleanContent = (text) => {
  if (!text) return "";

  let content = text;

  // A. Replaces 3 or more newlines with just 2
  content = content.replace(/\n{3,}/g, '\n\n'); 

  // B. Strip Markdown Bold (**) and Italics (_)
  content = content.replace(/\*\*|\_\_/g, '');

  // C. Convert long dashes to " - "
  content = content.replace(/[\u2014\u2015\u2013]/g, ' - ');

  // D. Normalize hyphens to [space]-[space]
  content = content.replace(/\s*-\s*/g, ' - ');

  // E. Fix compound words (e.g., "revenue - generating" -> "revenue-generating")
  content = content.replace(/([a-z])\s+-\s+([a-z])/gi, '$1-$2');

  // F. Fix run-on bullets (ensure a new line exists before every • symbol)
  content = content.replace(/([^\n])\s*•/g, '$1\n•');

  // G. Remove quotes around the whole post
  content = content.replace(/^["']|["']$/g, '');

  return content.trim();
};

// 4. Map the outputs
// Item 0 = GPT (Top Branch)
// Item 1 = Claude (Middle Branch)
// Item 2 = Gemini (Bottom Branch)
const options = {
  subject: topicTitle,
  strategy: "Multi-Angle Strategy (Standard, ROI, Lead-Gen)",
  row_number: rowId,
  option_gpt: cleanContent(items[0]?.json?.output),
  option_claude: cleanContent(items[1]?.json?.output),
  option_gemini: cleanContent(items[2]?.json?.output)
};

// Return as a single object for the Gmail node
return { data: options };

